import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import CanvasEditor from '../components/CanvasEditor'; // Adjust the import path as needed

const Editor = () => {
  const { id } = useParams();
  const [designData, setDesignData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!id) {
      setDesignData(null); // New blank design
      return;
    }

    setLoading(true);
    const token = localStorage.getItem('token');

    fetch(`http://localhost:5000/api/designs/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch design');
        return res.json();
      })
      .then((data) => {
        setDesignData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching design:", error);
        setDesignData(null);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div>Loading design...</div>;

  return <CanvasEditor initialData={designData || {}} />;
};

export default Editor;
