// src/pages/BlogPage.jsx
import React from "react";

const blogPosts = [
  {
    id: 1,
    title: "Welcome to Matty Design Tool!",
    author: "Kritika Ekka",
    date: "2025-10-04",
    summary: "Get started with our graphic editor. Learn how to use drawing tools, upload images, and export your designs easily.",
  },
  {
    id: 2,
    title: "Tips for Smooth Drawing",
    author: "Kritika Ekka",
    date: "2025-09-22",
    summary: "Best practices for using the pencil and eraser tools—how to avoid jagged lines and make your workflow more efficient.",
  },
  // Add more posts as needed
];

const BlogPage = () => (
  <div className="max-w-3xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold mb-8 text-center">Matty Blog</h1>
    <div className="space-y-8">
      {blogPosts.map(post => (
        <div key={post.id} className="bg-white rounded-xl shadow p-6">
          <h2 className="text-2xl font-semibold mb-2">{post.title}</h2>
          <div className="flex items-center text-gray-500 text-sm mb-3">
            <span>{post.author}</span>
            <span className="mx-2">•</span>
            <span>{new Date(post.date).toLocaleDateString()}</span>
          </div>
          <p className="text-lg mb-3">{post.summary}</p>
        </div>
      ))}
    </div>
  </div>
);

export default BlogPage;
