// src/pages/Reviews.jsx
import React from 'react';
import { motion } from 'framer-motion';

const Reviews = () => {
  const reviews = [
    { id: 1, name: 'Alice', rating: 5, comment: 'Amazing experience! Highly recommend.' },
    { id: 2, name: 'Bob', rating: 4, comment: 'Really helpful service and easy to use.' },
    { id: 3, name: 'Charlie', rating: 5, comment: 'Loved the design and usability!' },
    { id: 4, name: 'Dana', rating: 3, comment: 'It was okay, could be improved.' },
    { id: 5, name: 'Eve', rating: 4, comment: 'Very intuitive and user-friendly platform.' },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-700 via-blue-700 to-cyan-600 p-8">
      <h1 className="text-4xl md:text-5xl font-extrabold text-white text-center mb-10">
        What Users Are Saying
      </h1>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {reviews.map(review => (
          <motion.div
            key={review.id}
            whileHover={{ scale: 1.05 }}
            className="bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-6 shadow-lg transition-all duration-300"
          >
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-semibold text-white">{review.name}</h2>
              <span className="text-yellow-400">{'⭐'.repeat(review.rating)}</span>
            </div>
            <p className="text-gray-200 text-sm">{review.comment}</p>
          </motion.div>
        ))}
      </div>

      <div className="mt-12 text-center text-white/70 text-sm">
        <p>© 2025 Matty by Global Next Consulting India Pvt Ltd</p>
      </div>
    </div>
  );
};

export default Reviews;
