import React, { useRef, useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { Save, ImageIcon, Trash2, RotateCw, RotateCcw, Layers, Download, Menu } from "lucide-react";

// Helper: convert dataURL to Blob
function dataURLtoBlob(dataurl) {
  var arr = dataurl.split(","),
    mime = arr[0].match(/:(.*?);/)[1],
    bstr = atob(arr[1]),
    n = bstr.length,
    u8arr = new Uint8Array(n);
  while (n--) u8arr[n] = bstr.charCodeAt(n);
  return new Blob([u8arr], { type: mime });
}

// Draw regular polygons
function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const HANDLE_SIZE = 14;
let nextId = 1;

const getDefaultSize = (type, text, ctx) => {
  if (type === "rectangle") return { w: 140, h: 96 };
  if (type === "square" || type === "circle") return { w: 120, h: 120 };
  if (type === "oval") return { w: 140, h: 90 };
  if (type === "image") return { w: 240, h: 160 };
  if (type === "text") {
    ctx.font = "24px Arial";
    return { w: ctx.measureText(text).width, h: 36, fontSize: 24 };
  }
  return { w: 120, h: 80 };
};

export default function CanvasEditor({ initialData }) {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const { id: designId } = useParams();
  const navigate = useNavigate();

  // UI state
  const [designName, setDesignName] = useState("");
  const [selectedShape, setSelectedShape] = useState("rectangle");
  const [shapeColor, setShapeColor] = useState("#4f46e5");
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(22);
  const [textColor, setTextColor] = useState("#111827");
  const [pencilColor, setPencilColor] = useState("#333333");
  const [brushSize, setBrushSize] = useState(3);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [resizingHandle, setResizingHandle] = useState(null);
  const drawingPointsRef = useRef([]);

  // canvas transform (zoom/pan)
  const [zoom, setZoom] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });

  // history
  const [history, setHistory] = useState([{ elements: [], lines: [], title: "" }]);
  const [historyIndex, setHistoryIndex] = useState(0);

  // image cache
  const [imageCache, setImageCache] = useState({});

  const { elements, lines, title } = history[historyIndex];

  // load initialData
  useEffect(() => {
    setHistory([
      {
        elements: initialData?.jsonData?.elements || [],
        lines: initialData?.jsonData?.lines || [],
        title: initialData?.title || "",
      },
    ]);
    setHistoryIndex(0);
    setDesignName(initialData?.title || "");
  }, [initialData]);

  // cache images
  useEffect(() => {
    elements.forEach((el) => {
      if (el.type === "image" && el.src && !imageCache[el.id]) {
        const img = new window.Image();
        img.crossOrigin = "anonymous";
        img.src = el.src;
        img.onload = () => setImageCache((c) => ({ ...c, [el.id]: img }));
      }
    });
  }, [elements, imageCache]);

  // keyboard shortcuts
  useEffect(() => {
    const onKey = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === "z") {
        e.preventDefault();
        if (historyIndex > 0) setHistoryIndex((i) => i - 1);
      }
      if ((e.ctrlKey || e.metaKey) && e.key === "y") {
        e.preventDefault();
        if (historyIndex < history.length - 1) setHistoryIndex((i) => i + 1);
      }
      if (e.key === "Delete") {
        if (selectedIdx !== null) {
          const newElements = elements.filter((_, idx) => idx !== selectedIdx);
          saveState(newElements, lines, designName);
          setSelectedIdx(null);
        }
      }
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [historyIndex, history.length, selectedIdx, elements, lines, designName]);

  // draw
  useEffect(() => {
    if (!canvasRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // HiDPI support
    const DPR = window.devicePixelRatio || 1;
    const width = canvas.width;
    const height = canvas.height;
    canvas.width = Math.round(width * DPR);
    canvas.height = Math.round(height * DPR);
    canvas.style.width = `${width}px`;
    canvas.style.height = `${height}px`;
    ctx.scale(DPR, DPR);

    // clear
    ctx.clearRect(0, 0, width, height);

    // background grid
    ctx.save();
    const gridSize = 25 * zoom;
    ctx.fillStyle = "#ffffff";
    ctx.fillRect(0, 0, width, height);
    ctx.strokeStyle = "rgba(16,24,40,0.03)";
    ctx.lineWidth = 1;
    for (let x = (pan.x % gridSize); x < width; x += gridSize) {
      ctx.beginPath();
      ctx.moveTo(x, 0);
      ctx.lineTo(x, height);
      ctx.stroke();
    }
    for (let y = (pan.y % gridSize); y < height; y += gridSize) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(width, y);
      ctx.stroke();
    }
    ctx.restore();

    ctx.save();
    ctx.lineCap = "round";
    ctx.lineWidth = 2;

    // draw elements
    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w,
        h = el.h;
      const cx = el.x + w / 2;
      const cy = el.y + h / 2;
      ctx.translate(cx, cy);
      ctx.rotate(((el.rotation || 0) * Math.PI) / 180);
      ctx.translate(-cx, -cy);

      if (el.type === "image") {
        const img = imageCache[el.id];
        if (img) ctx.drawImage(img, el.x, el.y, w, h);
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42";
          ctx.lineWidth = 2;
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h);
        }
      } else if (el.type === "text") {
        ctx.font = `${el.fontSize || 24}px ${el.fontFamily || "Arial"}`;
        ctx.fillStyle = el.color || "#111";
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
        const boxW = el.w;
        const boxH = el.h || (el.fontSize || 24) + 10;
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42";
          ctx.strokeRect(el.x - 2, el.y - 2, boxW + 4, boxH + 4);
          drawHandles(ctx, el.x, el.y, boxW, boxH);
        }
      } else {
        ctx.fillStyle = el.color || "#333";
        ctx.strokeStyle = el.color || "#333";
        switch (el.type) {
          case "rectangle":
          case "square":
            ctx.fillRect(el.x, el.y, w, h);
            break;
          case "circle":
            ctx.beginPath();
            ctx.arc(el.x + w / 2, el.y + h / 2, w / 2, 0, 2 * Math.PI);
            ctx.fill();
            break;
          case "oval":
            ctx.save();
            ctx.translate(el.x + w / 2, el.y + h / 2);
            ctx.scale(1.5, 1);
            ctx.beginPath();
            ctx.arc(0, 0, h / 2, 0, 2 * Math.PI);
            ctx.restore();
            ctx.fill();
            break;
          case "triangle":
            ctx.beginPath();
            ctx.moveTo(el.x, el.y + h);
            ctx.lineTo(el.x + w, el.y + h);
            ctx.lineTo(el.x + w / 2, el.y);
            ctx.closePath();
            ctx.fill();
            break;
          case "parallelogram":
            ctx.beginPath();
            ctx.moveTo(el.x + 20, el.y + h);
            ctx.lineTo(el.x + w + 20, el.y + h);
            ctx.lineTo(el.x + w, el.y);
            ctx.lineTo(el.x, el.y);
            ctx.closePath();
            ctx.fill();
            break;
          case "rhombus":
            ctx.beginPath();
            ctx.moveTo(el.x + w / 2, el.y);
            ctx.lineTo(el.x + w, el.y + h / 2);
            ctx.lineTo(el.x + w / 2, el.y + h);
            ctx.lineTo(el.x, el.y + h / 2);
            ctx.closePath();
            ctx.fill();
            break;
          case "pentagon":
          case "hexagon":
          case "heptagon":
          case "octagon":
          case "nonagon":
          case "decagon": {
            const sides = {
              pentagon: 5,
              hexagon: 6,
              heptagon: 7,
              octagon: 8,
              nonagon: 9,
              decagon: 10,
            }[el.type];
            drawPolygon(ctx, el.x + w / 2, el.y + h / 2, Math.min(w, h) / 2, sides, el.color);
            break;
          }
          default:
            break;
        }
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42";
          ctx.lineWidth = 2;
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h);
        }
      }
      ctx.restore();
    });

    // draw lines (sketch)
    lines.forEach((line) => {
      ctx.beginPath();
      ctx.strokeStyle = line.mode === "eraser" ? "rgba(0,0,0,1)" : line.color || "#333";
      ctx.lineWidth = line.size || 2;
      ctx.globalCompositeOperation = line.mode === "eraser" ? "destination-out" : "source-over";
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.globalCompositeOperation = "source-over";
    });

    ctx.restore();
  }, [elements, lines, imageCache, selectedIdx, zoom, pan]);

  function drawHandles(ctx, x, y, w, h) {
    const handles = [
      [x - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
    ];
    handles.forEach(([hx, hy]) => {
      ctx.fillStyle = "#fff";
      ctx.strokeStyle = "#111827";
      ctx.lineWidth = 1;
      ctx.fillRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
      ctx.strokeRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
    });
  }

  const saveState = useCallback((newElements, newLines, newTitle = designName) => {
    setHistory((h) => {
      const copy = h.slice(0, historyIndex + 1);
      copy.push({ elements: JSON.parse(JSON.stringify(newElements)), lines: JSON.parse(JSON.stringify(newLines)), title: newTitle });
      setHistoryIndex(copy.length - 1);
      return copy;
    });
    setDesignName(newTitle);
  }, [historyIndex, designName]);

  const handleUndo = () => {
    if (historyIndex > 0) {
      setHistoryIndex((i) => i - 1);
      setSelectedIdx(null);
    }
  };
  const handleRedo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex((i) => i + 1);
      setSelectedIdx(null);
    }
  };

  const getHandleAt = (el, mouseX, mouseY) => {
    const { w, h } = el;
    const handles = [
      [el.x - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
    ];
    for (let i = 0; i < handles.length; i++) {
      const [hx, hy] = handles[i];
      if (mouseX >= hx && mouseX <= hx + HANDLE_SIZE && mouseY >= hy && mouseY <= hy + HANDLE_SIZE) return i;
    }
    return null;
  };

  const handleCanvasMouseDown = (e) => {
    if (!canvasRef.current) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const mouseX = Math.round(e.clientX - rect.left - pan.x);
    const mouseY = Math.round(e.clientY - rect.top - pan.y);

    let clicked = false;
    for (let i = elements.length - 1; i >= 0; i--) {
      const el = elements[i];
      const handleIdx = getHandleAt(el, mouseX, mouseY);
      if (i === selectedIdx && handleIdx !== null) {
        setResizingHandle(handleIdx);
        clicked = true;
        break;
      }
      if (mouseX >= el.x && mouseX <= el.x + el.w && mouseY >= el.y && mouseY <= el.y + el.h) {
        setSelectedIdx(i);
        setDragOffset({ x: mouseX - el.x, y: mouseY - el.y });
        setIsDragging(true);
        clicked = true;
        break;
      }
    }
    if (!clicked) setSelectedIdx(null);

    if (drawMode || eraserMode) {
      setIsDrawing(true);
      drawingPointsRef.current = [[mouseX, mouseY]];
    }
  };

  const handleCanvasMouseMove = (e) => {
    if (!canvasRef.current) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const mouseX = Math.round(e.clientX - rect.left - pan.x);
    const mouseY = Math.round(e.clientY - rect.top - pan.y);

    if (resizingHandle !== null && selectedIdx !== null) {
      const el = elements[selectedIdx];
      const newEl = { ...el };
      if (resizingHandle === 0) {
        newEl.x = mouseX;
        newEl.y = mouseY;
        newEl.w = el.w + (el.x - mouseX);
        newEl.h = el.h + (el.y - mouseY);
      } else if (resizingHandle === 1) {
        newEl.y = mouseY;
        newEl.w = mouseX - el.x;
        newEl.h = el.h + (el.y - mouseY);
      } else if (resizingHandle === 2) {
        newEl.x = mouseX;
        newEl.w = el.w + (el.x - mouseX);
        newEl.h = mouseY - el.y;
      } else if (resizingHandle === 3) {
        newEl.w = mouseX - el.x;
        newEl.h = mouseY - el.y;
      }
      newEl.w = Math.max(24, newEl.w);
      newEl.h = Math.max(24, newEl.h);
      if (el.type === "text") newEl.fontSize = Math.max(12, newEl.h - 6);
      const newElements = elements.map((s, idx) => (idx === selectedIdx ? newEl : s));
      setHistory((h) => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
      return;
    }

    if (isDragging && selectedIdx !== null) {
      const newElements = elements.map((s, idx) => (idx === selectedIdx ? { ...s, x: mouseX - dragOffset.x, y: mouseY - dragOffset.y } : s));
      setHistory((h) => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
      return;
    }

    if (isDrawing && (drawMode || eraserMode)) {
      const ctx = canvasRef.current.getContext("2d");
      const pts = drawingPointsRef.current;
      pts.push([mouseX, mouseY]);
      ctx.beginPath();
      ctx.moveTo(pts[pts.length - 2][0], pts[pts.length - 2][1]);
      ctx.lineTo(mouseX, mouseY);
      ctx.lineWidth = eraserMode ? eraserSize : brushSize;
      ctx.strokeStyle = eraserMode ? "rgba(0,0,0,1)" : pencilColor;
      ctx.globalCompositeOperation = eraserMode ? "destination-out" : "source-over";
      ctx.stroke();
      ctx.globalCompositeOperation = "source-over";
    }
  };

  const handleCanvasMouseUp = () => {
    if (isDrawing) {
      if (!canvasRef.current) return;
      const newLine = {
        id: nextId++,
        points: drawingPointsRef.current,
        color: pencilColor,
        size: eraserMode ? eraserSize : brushSize,
        mode: eraserMode ? "eraser" : "draw",
      };
      saveState(elements, [...lines, newLine], designName);
    }
    setResizingHandle(null);
    setIsDragging(false);
    setIsDrawing(false);
  };

  const handleAddShape = () => {
    if (!canvasRef.current) return;
    const ctx = canvasRef.current.getContext("2d");
    const { w, h } = getDefaultSize(selectedShape, "", ctx);
    const newElement = { id: nextId++, type: selectedShape, x: 180, y: 120, color: shapeColor, w, h, rotation: 0 };
    if (selectedShape === "image") return;
    saveState([...elements, newElement], lines, designName);
  };

  const [fontFamily, setFontFamily] = useState("Arial");
  const handleAddText = () => {
    if (!canvasRef.current) return;
    const text = prompt("Enter text to add:");
    if (!text) return;
    const ctx = canvasRef.current.getContext("2d");
    ctx.font = `24px ${fontFamily}`;
    const { w, h, fontSize } = getDefaultSize("text", text, ctx);
    const newText = { id: nextId++, type: "text", text, x: 120, y: 120, color: textColor, w, h, fontSize, fontFamily, rotation: 0 };
    saveState([...elements, newText], lines, designName);
  };

  const handleShapeColorChange = (e) => {
    setShapeColor(e.target.value);
    if (selectedIdx !== null) {
      const newElements = elements.map((el, idx) => (idx === selectedIdx ? { ...el, color: e.target.value } : el));
      saveState(newElements, lines, designName);
    }
  };

  const handleTextColorChange = (e) => {
    setTextColor(e.target.value);
    if (selectedIdx !== null && elements[selectedIdx]?.type === "text") {
      const newElements = elements.map((el, idx) => (idx === selectedIdx ? { ...el, color: e.target.value } : el));
      saveState(newElements, lines, designName);
    }
  };

  const handleUploadImage = () => fileInputRef.current.click();
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = function (event) {
      const newElement = { id: nextId++, type: "image", x: 50, y: 50, w: 240, h: 160, src: event.target.result, rotation: 0 };
      saveState([...elements, newElement], lines, designName);
    };
    reader.readAsDataURL(file);
    e.target.value = "";
  };

  const moveElement = (dir) => {
    if (selectedIdx === null) return;
    const newElements = [...elements];
    let idx = selectedIdx;
    if (dir === "up" && idx < newElements.length - 1) {
      [newElements[idx], newElements[idx + 1]] = [newElements[idx + 1], newElements[idx]];
      setSelectedIdx(idx + 1);
    }
    if (dir === "down" && idx > 0) {
      [newElements[idx], newElements[idx - 1]] = [newElements[idx - 1], newElements[idx]];
      setSelectedIdx(idx - 1);
    }
    saveState(newElements, lines, designName);
  };

  const clearCanvas = () => {
    setHistory([{ elements: [], lines: [], title: designName }]);
    setHistoryIndex(0);
    setSelectedIdx(null);
  };

  const handleDownload = () => {
    if (!canvasRef.current) return;
    const link = document.createElement("a");
    link.download = designName.trim() || "canvas.png";
    link.href = canvasRef.current.toDataURL();
    link.click();
  };

  const handleDeleteSelected = () => {
    if (selectedIdx !== null) {
      const newElements = elements.filter((_, idx) => idx !== selectedIdx);
      saveState(newElements, lines, designName);
      setSelectedIdx(null);
    }
  };

  const handleSaveToCloud = async () => {
    try {
      const token = localStorage.getItem("token");
      const imageDataURL = canvasRef.current.toDataURL("image/png");
      const imageBlob = dataURLtoBlob(imageDataURL);
      const formData = new FormData();
      formData.append("file", imageBlob);
      formData.append("upload_preset", "MattyDesignTool");
      const res = await fetch("https://api.cloudinary.com/v1_1/dvxazjesy/image/upload", { method: "POST", body: formData });
      const data = await res.json();
      const thumbnailUrl = data.secure_url;
      const jsonData = { elements, lines, title: designName };
      let response;
      if (designId) {
        response = await fetch(`http://localhost:5000/api/designs/${designId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
          body: JSON.stringify({ title: designName, thumbnailUrl, jsonData }),
        });
      } else {
        response = await fetch("http://localhost:5000/api/designs", {
          method: "POST",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
          body: JSON.stringify({ title: designName, thumbnailUrl, jsonData }),
        });
      }
      if (!response.ok) throw new Error("Failed to save design!");
      alert("Saved successfully âœ…");
    } catch (err) {
      alert("Save failed: " + err.message);
    }
  };

  // UI: go to dashboard when clicking logo
  const goToDashboard = () => navigate("/dashboard");

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-b from-slate-50 to-white">
      <motion.header
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.4 }}
        className="flex items-center justify-between px-6 py-4 bg-white/80 backdrop-blur sticky top-0 z-20 shadow-sm border-b"
      >
        <div className="flex items-center gap-4">
          <motion.button
            whileHover={{ scale: 1.03 }}
            whileTap={{ scale: 0.97 }}
            onClick={goToDashboard}
            className="flex items-center gap-3"
            aria-label="Go to dashboard"
          >
            <div className="rounded-md p-2 bg-gradient-to-br from-indigo-500 to-violet-600 text-white shadow-md">
              <svg width="28" height="28" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M3 12H21" stroke="white" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                <path d="M3 6H21" stroke="white" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                <path d="M3 18H21" stroke="white" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </div>
            <div className="text-2xl font-extrabold tracking-tight" style={{ fontFamily: '"Kablammo", system-ui' }}>MATTY</div>
          </motion.button>
        </div>

        <div className="flex items-center gap-3">
          <input
            className="border px-3 py-2 rounded-lg text-sm w-64 shadow-sm"
            placeholder="Enter design name"
            value={designName}
            onChange={(e) => {
              const newTitle = e.target.value;
              setDesignName(newTitle);
              saveState(elements, lines, newTitle);
            }}
          />
          <motion.button whileHover={{ scale: 1.03 }} className="flex items-center gap-2 px-3 py-2 rounded-lg bg-slate-100" onClick={handleSaveToCloud}>
            <Save size={16} /> Save
          </motion.button>
        </div>
      </motion.header>

      <div className="flex flex-1">
        <motion.aside initial={{ x: -20, opacity: 0 }} animate={{ x: 0, opacity: 1 }} transition={{ delay: 0.05 }} className="w-72 p-4 bg-white border-r">
          <div className="space-y-6">
            <div>
              <h3 className="text-lg font-semibold mb-2">Drawing Tools</h3>
              <div className="flex gap-2 mb-3">
                <button className={`flex-1 py-2 rounded ${drawMode && !eraserMode ? 'bg-indigo-50' : 'bg-slate-100'}`} onClick={() => { setDrawMode((s) => !s); setEraserMode(false); }}>Pencil</button>
                <button className={`flex-1 py-2 rounded ${eraserMode ? 'bg-indigo-50' : 'bg-slate-100'}`} onClick={() => { setEraserMode((s) => !s); setDrawMode(false); }}>Eraser</button>
              </div>
              <div className="flex items-center gap-3">
                <input type="color" value={pencilColor} onChange={(e) => setPencilColor(e.target.value)} className="w-9 h-9 p-0 border-0" />
                <div className="flex-1">
                  <input type="range" min={1} max={30} value={brushSize} onChange={(e) => setBrushSize(Number(e.target.value))} className="w-full" />
                  <div className="text-xs text-slate-500">Brush: {brushSize}px</div>
                </div>
              </div>
              <div className="mt-3">
                <label className="text-xs text-slate-500">Eraser size</label>
                <input type="range" min={6} max={80} value={eraserSize} onChange={(e) => setEraserSize(Number(e.target.value))} className="w-full" />
              </div>
            </div>

            <div>
              <h3 className="text-lg font-semibold mb-2">Shapes</h3>
              <select className="mb-3 p-2 border rounded w-full" value={selectedShape} onChange={(e) => setSelectedShape(e.target.value)}>
                <option value="rectangle">Rectangle</option>
                <option value="square">Square</option>
                <option value="circle">Circle</option>
                <option value="oval">Oval</option>
                <option value="triangle">Triangle</option>
                <option value="parallelogram">Parallelogram</option>
                <option value="pentagon">Pentagon</option>
                <option value="hexagon">Hexagon</option>
                <option value="octagon">Octagon</option>
              </select>
              <div className="flex items-center gap-3 mb-3">
                <input type="color" value={shapeColor} onChange={handleShapeColorChange} className="w-9 h-9 p-0 border-0" />
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={handleAddShape}>Add shape</button>
              </div>
            </div>

            <div>
              <h3 className="text-lg font-semibold mb-2">Text</h3>
              <select className="mb-3 p-2 border rounded w-full" value={fontFamily} onChange={(e) => setFontFamily(e.target.value)}>
                <option>Arial</option>
                <option>Times New Roman</option>
                <option>Georgia</option>
                <option>Courier New</option>
                <option>Verdana</option>
              </select>
              <div className="flex items-center gap-3 mb-3">
                <input type="color" value={textColor} onChange={handleTextColorChange} className="w-9 h-9 p-0 border-0" />
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={handleAddText}>Add text</button>
              </div>
            </div>

            <div>
              <h3 className="text-lg font-semibold mb-2">Images & File</h3>
              <div className="flex gap-2">
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={handleUploadImage}><ImageIcon size={16} className="inline-block mr-2"/> Upload</button>
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={handleDownload}><Download size={16} className="inline-block mr-2"/> Export</button>
              </div>
            </div>

            <div className="pt-2 border-t">
              <div className="flex gap-2">
                <button className="flex-1 py-2 rounded bg-red-50 text-red-700" onClick={handleDeleteSelected}><Trash2 size={14} className="inline-block mr-2"/>Delete</button>
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={clearCanvas}><RotateCcw size={14} className="inline-block mr-2"/>Clear</button>
              </div>
              <div className="flex gap-2 mt-3">
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={handleUndo}>Undo</button>
                <button className="flex-1 py-2 rounded bg-slate-100" onClick={handleRedo}>Redo</button>
              </div>
            </div>

          </div>
        </motion.aside>

        <main className="flex-1 p-6 flex flex-col items-center justify-center">
          <div className="w-full max-w-6xl bg-white rounded shadow-lg p-4">
            <div className="flex gap-3 items-center mb-3 justify-between">
              <div className="text-sm text-slate-500">Canvas</div>
              <div className="text-xs text-slate-400">Tip: Use Delete to remove selected, Ctrl/Cmd+Z to undo</div>
            </div>
            <div className="border rounded-md overflow-hidden">
              <div className="relative bg-neutral-50" style={{ height: 720 }}>
                <canvas
                  ref={canvasRef}
                  width={1200}
                  height={720}
                  style={{ display: 'block', width: '100%', height: '720px', background: '#fff' }}
                  onMouseDown={handleCanvasMouseDown}
                  onMouseMove={handleCanvasMouseMove}
                  onMouseUp={handleCanvasMouseUp}
                  onMouseLeave={handleCanvasMouseUp}
                />
                <input type="file" accept="image/*" ref={fileInputRef} style={{ display: 'none' }} onChange={handleFileChange} />
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
