import React, { useRef, useEffect, useState } from 'react';

const CLOUDINARY_URL = 'https://api.cloudinary.com/v1_1/<your-cloud-name>/upload';
const UPLOAD_PRESET = '<your-upload-preset>';

function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const HANDLE_SIZE = 16;
let nextId = 1;

const CanvasEditor = ({ initialData }) => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);

  const [file, setFile] = useState(null);
  const [thumbnailUrl, setThumbnailUrl] = useState('');
  const [uploading, setUploading] = useState(false);

  const [selectedShape, setSelectedShape] = useState('rectangle');
  const [shapeColor, setShapeColor] = useState('#4f46e5');
  const [textColor, setTextColor] = useState('#111827');
  const [pencilColor, setPencilColor] = useState('#333');
  const [brushSize, setBrushSize] = useState(2);
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(20);

  const [history, setHistory] = useState([
    {
      elements: initialData?.jsonData?.elements || [],
      lines: initialData?.jsonData?.lines || [],
    },
  ]);
  const [historyIndex, setHistoryIndex] = useState(0);

  const { elements, lines } = history[historyIndex];
  const [imageCache, setImageCache] = useState({});
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [resizingHandle, setResizingHandle] = useState(null);
  const drawingPointsRef = useRef([]);

  // Preload images for rendering
  useEffect(() => {
    elements.forEach((el) => {
      if (el.type === 'image' && el.src && !imageCache[el.id]) {
        const img = new window.Image();
        img.src = el.src;
        img.onload = () => setImageCache((c) => ({ ...c, [el.id]: img }));
      }
    });
  }, [elements, imageCache]);

  // Draw elements and lines on canvas
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineCap = 'round';
    ctx.lineWidth = 2;

    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w,
        h = el.h;
      const cx = el.x + w / 2;
      const cy = el.y + h / 2;
      ctx.translate(cx, cy);
      const rotationRad = (el.rotation || 0) * Math.PI / 180;
      ctx.rotate(rotationRad);
      ctx.translate(-cx, -cy);

      if (el.type === 'image') {
        const img = imageCache[el.id];
        if (img) {
          ctx.drawImage(img, el.x, el.y, w, h);
          if (idx === selectedIdx) {
            ctx.strokeStyle = '#f59e42';
            ctx.lineWidth = 2;
            ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
            drawHandles(ctx, el.x, el.y, w, h);
          }
        }
      } else if (el.type === 'text') {
        ctx.font = `${el.fontSize || 24}px Arial`;
        ctx.fillStyle = el.color;
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
        const boxW = el.w;
        const boxH = el.h || (el.fontSize || 24) + 8;
        if (idx === selectedIdx) {
          ctx.strokeStyle = '#f59e42';
          ctx.strokeRect(el.x - 2, el.y - 2, boxW + 4, boxH + 4);
          drawHandles(ctx, el.x, el.y, boxW, boxH);
        }
      } else {
        ctx.fillStyle = el.color;
        ctx.strokeStyle = el.color;
        // Add your shapes drawing switch-case here if needed
        if (idx === selectedIdx) {
          ctx.strokeStyle = '#f59e42';
          ctx.lineWidth = 2;
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h);
        }
      }
      ctx.restore();
    });

    lines.forEach((line) => {
      ctx.beginPath();
      ctx.strokeStyle = line.mode === 'eraser' ? 'rgba(0,0,0,1)' : line.color || '#333';
      ctx.lineWidth = line.size || 2;
      ctx.globalCompositeOperation = line.mode === 'eraser' ? 'destination-out' : 'source-over';
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.globalCompositeOperation = 'source-over';
    });
  }, [elements, lines, imageCache, selectedIdx]);

  function drawHandles(ctx, x, y, w, h) {
    const handles = [
      [x - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
    ];
    handles.forEach(([hx, hy]) => {
      ctx.fillStyle = '#fff';
      ctx.strokeStyle = '#333';
      ctx.lineWidth = 1;
      ctx.fillRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
      ctx.strokeRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
    });
  }

  const handleCanvasMouseDown = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    let clicked = false;
    for (let i = elements.length - 1; i >= 0; i--) {
      const el = elements[i];
      const w = el.w,
        h = el.h;
      const handleIdx = getHandleAt(el, mouseX, mouseY);
      if (i === selectedIdx && handleIdx !== null) {
        setResizingHandle(handleIdx);
        clicked = true;
        break;
      }
      if (mouseX >= el.x && mouseX <= el.x + w && mouseY >= el.y && mouseY <= el.y + h) {
        setSelectedIdx(i);
        setDragOffset({ x: mouseX - el.x, y: mouseY - el.y });
        setIsDragging(true);
        clicked = true;
        break;
      }
    }
    if (!clicked) setSelectedIdx(null);

    if (drawMode || eraserMode) {
      setIsDrawing(true);
      drawingPointsRef.current = [[mouseX, mouseY]];
    }
  };

  const handleCanvasMouseMove = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;

    if (resizingHandle !== null && selectedIdx !== null) {
      const el = elements[selectedIdx];
      let newEl = { ...el };
      if (resizingHandle === 0) {
        newEl.x = mouseX;
        newEl.y = mouseY;
        newEl.w = el.w + (el.x - mouseX);
        newEl.h = el.h + (el.y - mouseY);
      } else if (resizingHandle === 1) {
        newEl.y = mouseY;
        newEl.w = mouseX - el.x;
        newEl.h = el.h + (el.y - mouseY);
      } else if (resizingHandle === 2) {
        newEl.x = mouseX;
        newEl.w = el.w + (el.x - mouseX);
        newEl.h = mouseY - el.y;
      } else if (resizingHandle === 3) {
        newEl.w = mouseX - el.x;
        newEl.h = mouseY - el.y;
      }
      newEl.w = Math.max(20, newEl.w);
      newEl.h = Math.max(20, newEl.h);
      if (el.type === 'text') {
        newEl.fontSize = Math.max(12, newEl.h - 8);
      }
      const newElements = elements.map((s, idx) => (idx === selectedIdx ? newEl : s));
      setHistory((h) => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
      return;
    }

    if (isDragging && selectedIdx !== null) {
      const el = elements[selectedIdx];
      const newElements = elements.map((s, idx) =>
        idx === selectedIdx ? { ...s, x: mouseX - dragOffset.x, y: mouseY - dragOffset.y } : s
      );
      setHistory((h) => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
    }

    if (isDrawing && (drawMode || eraserMode)) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      const points = drawingPointsRef.current;
      points.push([mouseX, mouseY]);
      ctx.beginPath();
      ctx.moveTo(points[points.length - 2][0], points[points.length - 2][1]);
      ctx.lineTo(mouseX, mouseY);
      ctx.lineWidth = eraserMode ? eraserSize : brushSize;
      ctx.strokeStyle = eraserMode ? 'rgba(0,0,0,1)' : pencilColor;
      ctx.globalCompositeOperation = eraserMode ? 'destination-out' : 'source-over';
      ctx.stroke();
      ctx.globalCompositeOperation = 'source-over';
    }
  };

  const handleCanvasMouseUp = () => {
    if (resizingHandle !== null) {
      setResizingHandle(null);
      saveState(elements, lines);
      return;
    }
    if (isDragging && selectedIdx !== null) {
      saveState(elements, lines);
      setIsDragging(false);
    }
    if (isDrawing) {
      setIsDrawing(false);
    }
  };

  const handleFileChange = (e) => {
    const uploadFile = e.target.files[0];
    if (!uploadFile) return;
    setFile(uploadFile);
  };

  const handleUploadImage = async () => {
    if (!file) {
      alert('Please select a file first');
      return;
    }
    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('upload_preset', UPLOAD_PRESET);

    try {
      const res = await fetch(CLOUDINARY_URL, {
        method: 'POST',
        body: formData,
      });
      const data = await res.json();
      setThumbnailUrl(data.secure_url);
      alert('Image uploaded successfully');
    } catch (err) {
      console.error('Upload Error:', err);
      alert('Image upload failed');
    } finally {
      setUploading(false);
      setFile(null);
      if (fileInputRef.current) fileInputRef.current.value = '';
    }
  };

  const getHandleAt = (el, mouseX, mouseY) => {
    const w = el.w,
      h = el.h;
    const handles = [
      [el.x - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
    ];
    for (let i = 0; i < handles.length; i++) {
      const [hx, hy] = handles[i];
      if (mouseX >= hx && mouseX <= hx + HANDLE_SIZE && mouseY >= hy && mouseY <= hy + HANDLE_SIZE) {
        return i;
      }
    }
    return null;
  };

  const saveState = (newElements, newLines) => {
    const newHistory = history.slice(0, historyIndex + 1);
    newHistory.push({
      elements: JSON.parse(JSON.stringify(newElements)),
      lines: JSON.parse(JSON.stringify(newLines)),
    });
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
  };

  const handleUndo = () => {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      setSelectedIdx(null);
    }
  };

  const handleRedo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      setSelectedIdx(null);
    }
  };

  const moveElement = (dir) => {
    if (selectedIdx === null) return;
    const newElements = [...elements];
    let idx = selectedIdx;
    if (dir === 'up' && idx < newElements.length - 1) {
      [newElements[idx], newElements[idx + 1]] = [newElements[idx + 1], newElements[idx]];
      setSelectedIdx(idx + 1);
    }
    if (dir === 'down' && idx > 0) {
      [newElements[idx], newElements[idx - 1]] = [newElements[idx - 1], newElements[idx]];
      setSelectedIdx(idx - 1);
    }
    saveState(newElements, lines);
  };

  const clearCanvas = () => {
    setHistory([{ elements: [], lines: [] }]);
    setHistoryIndex(0);
    setSelectedIdx(null);
  };

  const handleDownload = () => {
    const canvas = canvasRef.current;
    const link = document.createElement('a');
    link.download = 'canvas.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  const handleRotationChange = (e) => {
    if (selectedIdx === null) return;
    const angle = Number(e.target.value);
    const newElements = elements.map((el, idx) =>
      idx === selectedIdx ? { ...el, rotation: angle } : el
    );
    saveState(newElements, lines);
  };

  return (
    <div className="flex flex-col h-screen">
      <header className="flex justify-between items-center p-4 bg-gray-100 border-b">
        <div className="text-xl font-bold">MATTY</div>
        <div className="flex gap-2">{/* Social media icons */}</div>
        <button className="px-4 py-2">About Us</button>
      </header>
      <div className="flex gap-4 p-2 bg-gray-50 border-b">
        <button onClick={clearCanvas}>Clear All</button>
        <button onClick={handleUndo} disabled={historyIndex === 0}>
          Undo
        </button>
        <button onClick={handleRedo} disabled={historyIndex === history.length - 1}>
          Redo
        </button>
        <button onClick={() => moveElement('up')} disabled={selectedIdx === null || selectedIdx >= elements.length - 1}>
          Move Up
        </button>
        <button onClick={() => moveElement('down')} disabled={selectedIdx === null || selectedIdx <= 0}>
          Move Down
        </button>
        <label className="ml-4 flex items-center gap-2">
          Rotation:
          <input
            type="range"
            min={0}
            max={360}
            value={selectedIdx !== null ? elements[selectedIdx].rotation || 0 : 0}
            onChange={handleRotationChange}
          />
          <span>{selectedIdx !== null ? elements[selectedIdx].rotation || 0 : 0}Â°</span>
        </label>
      </div>
      <div className="flex flex-1">
        <aside className="flex flex-col gap-6 p-4 bg-gray-50 border-r min-w-[240px]">
          {/* Drawing Tools UI remains unchanged */}

          {/* Shapes UI remains unchanged */}

          {/* Text UI remains unchanged */}

          <div className="bg-white rounded-lg shadow-sm p-4">
            <button
              className="w-full mb-2 py-2 rounded bg-gray-100 hover:bg-blue-50"
              onClick={() => fileInputRef.current.click()}
            >
              Upload Image
            </button>
            <input
              type="file"
              accept="image/*"
              ref={fileInputRef}
              style={{ display: 'none' }}
              onChange={handleFileChange}
            />
            <button
              className="w-full py-2 rounded bg-gray-100 hover:bg-blue-50"
              onClick={handleUploadImage}
              disabled={!file || uploading}
            >
              {uploading ? 'Uploading...' : 'Upload to Cloudinary'}
            </button>
            {thumbnailUrl && <img src={thumbnailUrl} alt="Uploaded thumbnail" className="mt-2 max-w-full" />}
          </div>

          <div className="bg-white rounded-lg shadow-sm p-4 mt-6">
            <button className="w-full py-2 rounded bg-gray-100 hover:bg-blue-50" onClick={handleDownload}>
              Download
            </button>
          </div>
        </aside>

        <main className="flex-1 flex items-center justify-center">
          <canvas
            ref={canvasRef}
            width={1000}
            height={700}
            style={{ background: '#fff', border: '1px solid #eee', marginTop: '-250px' }}
            onMouseDown={handleCanvasMouseDown}
            onMouseMove={handleCanvasMouseMove}
            onMouseUp={handleCanvasMouseUp}
            onMouseLeave={handleCanvasMouseUp}
          />
        </main>
      </div>
    </div>
  );
};

export default CanvasEditor;
