import React, { useRef, useEffect, useState } from 'react';

function drawPolygon(ctx, x, y, radius, sides) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fill();
}

const CanvasEditor = () => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastPoint, setLastPoint] = useState(null);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [selectedShape, setSelectedShape] = useState('rectangle');
  const [shapeColor, setShapeColor] = useState('#4f46e5');
  const [drawMode, setDrawMode] = useState(false);

useEffect(() => {
  const canvas = canvasRef.current;
  const ctx = canvas.getContext('2d');
  // ...draw shapes, etc.
  texts.forEach(({ text, x, y, color }) => {
    ctx.font = '24px Arial';
    ctx.fillStyle = color;
    ctx.fillText(text, x, y);
  });
}, [texts, ...otherDependencies]);




  // Drawing logic
  const startDrawing = (e) => {
    if (!drawMode) return;
    setIsDrawing(true);
    setLastPoint({
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    });
  };

  const draw = (e) => {
    if (!isDrawing || !drawMode) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(lastPoint.x, lastPoint.y);
    ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    ctx.stroke();
    setLastPoint({
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    });
  };

  const stopDrawing = () => {
    if (!drawMode) return;
    setIsDrawing(false);
    setLastPoint(null);
    // Save current canvas state to history
    const canvas = canvasRef.current;
    const dataUrl = canvas.toDataURL();
    setHistory((prev) => [...prev, dataUrl]);
    setRedoStack([]);
  };

  // Toolbar and sidebar handlers
  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setHistory([]);
    setRedoStack([]);
  };

  const handleUndo = () => {
    if (history.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const newHistory = [...history];
    const last = newHistory.pop();
    setRedoStack((prev) => [...prev, last]);
    setHistory(newHistory);
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    if (newHistory.length > 0) {
      const img = new window.Image();
      img.src = newHistory[newHistory.length - 1];
      img.onload = () => ctx.drawImage(img, 0, 0);
    }
  };

  const handleRedo = () => {
    if (redoStack.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const newRedo = [...redoStack];
    const redoImg = newRedo.pop();
    setHistory((prev) => [...prev, redoImg]);
    setRedoStack(newRedo);
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const img = new window.Image();
    img.src = redoImg;
    img.onload = () => ctx.drawImage(img, 0, 0);
  };

  // --- Upload Image ---
  const handleUploadImage = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = function (event) {
      const img = new window.Image();
      img.onload = function () {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(img, 50, 50, 200, 200);
        const dataUrl = canvas.toDataURL();
        setHistory((prev) => [...prev, dataUrl]);
        setRedoStack([]);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(file);
    e.target.value = '';
  };

  // --- Add Shape ---
  const handleAddShape = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = shapeColor;
    ctx.strokeStyle = shapeColor;
    // Rectangle
    if (selectedShape === 'rectangle') {
      ctx.fillRect(300, 100, 120, 80);
    }
    // Square
    else if (selectedShape === 'square') {
      ctx.fillRect(300, 100, 100, 100);
    }
    // Circle
    else if (selectedShape === 'circle') {
      ctx.beginPath();
      ctx.arc(360, 140, 50, 0, 2 * Math.PI);
      ctx.fill();
    }
    // Oval
    else if (selectedShape === 'oval') {
      ctx.save();
      ctx.translate(360, 140);
      ctx.scale(1.5, 1);
      ctx.beginPath();
      ctx.arc(0, 0, 40, 0, 2 * Math.PI);
      ctx.restore();
      ctx.fill();
    }
    // Triangle
    else if (selectedShape === 'triangle') {
      ctx.beginPath();
      ctx.moveTo(310, 180);
      ctx.lineTo(410, 180);
      ctx.lineTo(360, 80);
      ctx.closePath();
      ctx.fill();
    }
    // Parallelogram
    else if (selectedShape === 'parallelogram') {
      ctx.beginPath();
      ctx.moveTo(320, 180);
      ctx.lineTo(440, 180);
      ctx.lineTo(400, 120);
      ctx.lineTo(280, 120);
      ctx.closePath();
      ctx.fill();
    }
    // Rhombus
    else if (selectedShape === 'rhombus') {
      ctx.beginPath();
      ctx.moveTo(360, 80);
      ctx.lineTo(420, 140);
      ctx.lineTo(360, 200);
      ctx.lineTo(300, 140);
      ctx.closePath();
      ctx.fill();
    }
    // Trapezoid
    else if (selectedShape === 'trapezoid') {
      ctx.beginPath();
      ctx.moveTo(310, 180);
      ctx.lineTo(410, 180);
      ctx.lineTo(390, 120);
      ctx.lineTo(330, 120);
      ctx.closePath();
      ctx.fill();
    }
    // Kite
    else if (selectedShape === 'kite') {
      ctx.beginPath();
      ctx.moveTo(360, 90);
      ctx.lineTo(390, 140);
      ctx.lineTo(360, 190);
      ctx.lineTo(330, 140);
      ctx.closePath();
      ctx.fill();
    }
    // Regular polygons
    else if (selectedShape === 'pentagon') {
      drawPolygon(ctx, 360, 140, 50, 5);
    }
    else if (selectedShape === 'hexagon') {
      drawPolygon(ctx, 360, 140, 50, 6);
    }
    else if (selectedShape === 'heptagon') {
      drawPolygon(ctx, 360, 140, 50, 7);
    }
    else if (selectedShape === 'octagon') {
      drawPolygon(ctx, 360, 140, 50, 8);
    }
    else if (selectedShape === 'nonagon') {
      drawPolygon(ctx, 360, 140, 50, 9);
    }
    else if (selectedShape === 'decagon') {
      drawPolygon(ctx, 360, 140, 50, 10);
    }
    // Save state after adding shape
    const dataUrl = canvas.toDataURL();
    setHistory((prev) => [...prev, dataUrl]);
    setRedoStack([]);
  };

  // --- Add Text ---
const handleAddText = () => {
  const text = prompt('Enter text to add:');
  if (!text) return;
  setTexts(prev => [...prev, { text, x: 100, y: 400, color: textColor }]);
};
  const [texts, setTexts] = useState([]); // Array of {text, x, y, color}
const [selectedTextIndex, setSelectedTextIndex] = useState(null);
const [textColor, setTextColor] = useState('#111827');

  // --- Download ---
  const handleDownload = () => {
    const canvas = canvasRef.current;
    const link = document.createElement('a');
    link.download = 'canvas.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="flex flex-col h-screen">
      {/* Header Row */}
      <header className="flex justify-between items-center p-4 bg-gray-100 border-b">
        <div className="text-xl font-bold">MATTY</div>
        <div className="flex gap-2">{/* Social media icons */}</div>
        <button className="px-4 py-2">About Us</button>
      </header>
      {/* Tools Bar */}
      <div className="flex gap-4 p-2 bg-gray-50 border-b">
        <button onClick={clearCanvas}>Clear All</button>
        <button onClick={handleUndo}>Undo</button>
        <button onClick={handleRedo}>Redo</button>
      </div>
      {/* Main Workspace */}
      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="flex flex-col gap-4 p-4 bg-gray-50 border-r">
          <button
            className={drawMode ? 'font-bold underline' : ''}
            onClick={() => setDrawMode(true)}
          >
            Pencil/Draw
          </button>
          <label>
            <span className="block mb-1">Shape</span>
            <select
              value={selectedShape}
              onChange={e => {
                setSelectedShape(e.target.value);
                setDrawMode(false);
              }}
              className="mb-2 p-1 border rounded"
            >
              <option value="rectangle">Rectangle</option>
              <option value="square">Square</option>
              <option value="circle">Circle</option>
              <option value="oval">Oval</option>
              <option value="triangle">Triangle</option>
              <option value="parallelogram">Parallelogram</option>
              <option value="rhombus">Rhombus</option>
              <option value="trapezoid">Trapezoid</option>
              <option value="kite">Kite</option>
              <option value="pentagon">Pentagon</option>
              <option value="hexagon">Hexagon</option>
              <option value="heptagon">Heptagon</option>
              <option value="octagon">Octagon</option>
              <option value="nonagon">Nonagon</option>
              <option value="decagon">Decagon</option>
            </select>
          </label>
          <label>
            <span className="block mb-1">Shape Color</span>
            <input
              type="color"
              value={shapeColor}
              onChange={e => setShapeColor(e.target.value)}
              className="w-8 h-8 p-0 border-0"
            />
          </label>
          <label>
  <span className="block mb-1">Text Color</span>
  <input
    type="color"
    value={textColor}
    onChange={e => setTextColor(e.target.value)}
    className="w-8 h-8 p-0 border-0"
  />
</label>
          <button onClick={handleUploadImage}>Upload Image</button>
          <button onClick={handleAddShape}>Add Shape</button>
          <button onClick={handleAddText}>Add Text</button>
          <button onClick={handleDownload}>Download</button>
          <input
            type="file"
            accept="image/*"
            ref={fileInputRef}
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
        </aside>
        <main className="flex-1 flex items-center justify-center">
          <canvas
            ref={canvasRef}
            width={800}
            height={500}
            style={{ background: '#fff', border: '1px solid #eee' }}
            onMouseDown={startDrawing}
            onMouseMove={draw}
            onMouseUp={stopDrawing}
            onMouseLeave={stopDrawing}
          />
        </main>
      </div>
    </div>
  );
};

export default CanvasEditor;
