import React, { useRef, useEffect, useState } from "react";

// Draw regular polygons
function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const HANDLE_SIZE = 16;
let nextId = 1;

const getDefaultSize = (type, text, ctx) => {
  if (type === "rectangle") return { w: 120, h: 80 };
  if (type === "square" || type === "circle") return { w: 100, h: 100 };
  if (type === "oval") return { w: 120, h: 80 };
  if (type === "image") return { w: 200, h: 200 };
  if (type === "text") {
    ctx.font = "24px Arial";
    return { w: ctx.measureText(text).width, h: 32, fontSize: 24 };
  }
  return { w: 120, h: 80 };
};

const CanvasEditor = ({ initialData }) => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);

  const [designName, setDesignName] = useState(initialData?.title || "");
  const [selectedShape, setSelectedShape] = useState("rectangle");
  const [shapeColor, setShapeColor] = useState("#4f46e5");
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(20);
  const [textColor, setTextColor] = useState("#111827");
  const [pencilColor, setPencilColor] = useState("#333");
  const [brushSize, setBrushSize] = useState(2);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [resizingHandle, setResizingHandle] = useState(null);
  const drawingPointsRef = useRef([]);

  // History for undo/redo
  const [history, setHistory] = useState([
    {
      elements: initialData?.jsonData?.elements || [],
      lines: initialData?.jsonData?.lines || [],
      title: initialData?.title || "",
    },
  ]);
  const [historyIndex, setHistoryIndex] = useState(0);
  const [imageCache, setImageCache] = useState({});

  const { elements, lines, title } = history[historyIndex];

  useEffect(() => {
    setDesignName(title || "");
  }, [title]);

  useEffect(() => {
    elements.forEach(el => {
      if (el.type === "image" && el.src && !imageCache[el.id]) {
        const img = new window.Image();
        img.src = el.src;
        img.onload = () => setImageCache(c => ({ ...c, [el.id]: img }));
      }
    });
  }, [elements, imageCache]);

  // Redraw canvas on change
  useEffect(() => {
    if (!canvasRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineCap = "round";
    ctx.lineWidth = 2;

    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w,
        h = el.h;
      const cx = el.x + w / 2,
        cy = el.y + h / 2;
      ctx.translate(cx, cy);
      ctx.rotate(((el.rotation || 0) * Math.PI) / 180);
      ctx.translate(-cx, -cy);

      if (el.type === "image") {
        const img = imageCache[el.id];
        if (img) ctx.drawImage(img, el.x, el.y, w, h);
      } else if (el.type === "text") {
        ctx.font = `${el.fontSize || 24}px Arial`;
        ctx.fillStyle = el.color;
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
      } else {
        ctx.fillStyle = el.color;
        ctx.strokeStyle = el.color;
        if (el.type === "rectangle" || el.type === "square") {
          ctx.fillRect(el.x, el.y, w, h);
        } else if (el.type === "circle") {
          ctx.beginPath();
          ctx.arc(el.x + w / 2, el.y + h / 2, w / 2, 0, 2 * Math.PI); ctx.fill();
        } else if (el.type === "oval") {
          ctx.save();
          ctx.translate(el.x + w / 2, el.y + h / 2);
          ctx.scale(1.5, 1);
          ctx.beginPath();
          ctx.arc(0, 0, h / 2, 0, 2 * Math.PI);
          ctx.restore();
          ctx.fill();
        } else if (["pentagon","hexagon","heptagon","octagon","nonagon","decagon"].includes(el.type)) {
          const sides = {pentagon: 5, hexagon: 6, heptagon: 7, octagon: 8, nonagon: 9, decagon: 10}[el.type];
          drawPolygon(ctx, el.x + w/2, el.y + h/2, Math.min(w,h)/2, sides, el.color);
        }
      }
      ctx.restore();
    });

    lines.forEach(line => {
      ctx.beginPath();
      ctx.strokeStyle = line.mode === "eraser" ? "rgba(0,0,0,1)" : line.color || "#333";
      ctx.lineWidth = line.size || 2;
      ctx.globalCompositeOperation = line.mode === "eraser" ? "destination-out" : "source-over";
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.globalCompositeOperation = "source-over";
    });
  }, [elements, lines, imageCache, selectedIdx]);

  // Utility
  const saveState = (newElements, newLines, newTitle = designName) => {
    const newHistory = history.slice(0, historyIndex + 1);
    newHistory.push({
      elements: JSON.parse(JSON.stringify(newElements)),
      lines: JSON.parse(JSON.stringify(newLines)),
      title: newTitle,
    });
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
    setDesignName(newTitle);
  };

  // CORE: Mouse handlers
  const handleCanvasMouseDown = (e) => {
    if (!canvasRef.current) return;
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    setIsDrawing(true);
    drawingPointsRef.current = [[mouseX, mouseY]];
  };

  const handleCanvasMouseMove = (e) => {
    if (!canvasRef.current || !isDrawing) return;
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    const ctx = canvasRef.current.getContext("2d");
    const points = drawingPointsRef.current;
    points.push([mouseX, mouseY]);
    ctx.beginPath();
    ctx.moveTo(points[points.length - 2][0], points[points.length - 2][1]);
    ctx.lineTo(mouseX, mouseY);
    ctx.lineWidth = drawMode ? brushSize : eraserSize;
    ctx.strokeStyle = drawMode ? pencilColor : "rgba(0,0,0,1)";
    ctx.globalCompositeOperation = eraserMode ? "destination-out" : "source-over";
    ctx.stroke();
    ctx.globalCompositeOperation = "source-over";
  };

  const handleCanvasMouseUp = () => {
    if (isDrawing && canvasRef.current) {
      const newLine = {
        id: nextId++,
        points: drawingPointsRef.current,
        color: drawMode ? pencilColor : "rgba(0,0,0,1)",
        size: drawMode ? brushSize : eraserSize,
        mode: eraserMode ? "eraser" : "draw",
      };
      saveState(elements, [...lines, newLine], designName);
      setIsDrawing(false);
    }
  };

  // Image upload
  const handleUploadImage = () => {
    fileInputRef.current.click();
  };
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new window.FileReader();
    reader.onload = function (event) {
      const newElement = {
        id: nextId++,
        type: "image",
        x: 50,
        y: 50,
        w: 200,
        h: 200,
        src: event.target.result,
        rotation: 0,
      };
      saveState([...elements, newElement], lines, designName);
    };
    reader.readAsDataURL(file);
    e.target.value = ""; // Reset input
  };

  // Save to cloud with thumbnail
  const handleSaveToCloud = async () => {
    if (!canvasRef.current) {
      alert("Canvas is not ready yet!");
      return;
    }
    const imageDataURL = canvasRef.current.toDataURL("image/png");
    try {
      const response = await fetch("http://localhost:5000/api/designs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          image: imageDataURL,
          title: designName.trim() || "Untitled Design",
          jsonData: { elements, lines },
        }),
      });
      const data = await response.json();
      if (response.ok) {
        alert("Design saved and uploaded successfully!");
      } else {
        alert("Failed to upload design: " + (data.message || "Unknown error"));
      }
    } catch (error) {
      alert("Upload failed, please try again.");
    }
  };

  // Undo/Redo
  const handleUndo = () => {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      setSelectedIdx(null);
    }
  };
  const handleRedo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      setSelectedIdx(null);
    }
  };

  return (
    <div className="flex flex-col h-screen">
      <header className="p-3 font-bold text-lg bg-gray-100 border-b">Canvas Editor</header>
      <div style={{ padding: 8 }}>
        <button onClick={handleUndo} disabled={historyIndex === 0}>Undo</button>
        <button onClick={handleRedo} disabled={historyIndex === history.length - 1}>Redo</button>
        <button onClick={handleUploadImage}>Upload Image</button>
        <input type="file" accept="image/*" ref={fileInputRef} style={{ display: "none" }} onChange={handleFileChange}/>
        <button onClick={handleSaveToCloud}>Save to Cloud</button>
      </div>
      <main className="flex-1 flex items-center justify-center">
        <canvas
          ref={canvasRef}
          width={1000}
          height={700}
          style={{ background: "#fff", border: "1px solid #eee", minHeight: 300 }}
          onMouseDown={handleCanvasMouseDown}
          onMouseMove={handleCanvasMouseMove}
          onMouseUp={handleCanvasMouseUp}
          onMouseLeave={handleCanvasMouseUp}
        />
      </main>
    </div>
  );
};

export default CanvasEditor;
