import React, { useRef, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

function dataURLtoBlob(dataurl) {
  var arr = dataurl.split(","),
    mime = arr[0].match(/:(.*?);/)[1],
    bstr = atob(arr[1]),
    n = bstr.length,
    u8arr = new Uint8Array(n);
  while (n--) u8arr[n] = bstr.charCodeAt(n);
  return new Blob([u8arr], { type: mime });
}

function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const HANDLE_SIZE = 16;
let nextId = 1;

const getDefaultSize = (type, text, ctx) => {
  if (type === "rectangle") return { w: 120, h: 80 };
  if (type === "square" || type === "circle") return { w: 100, h: 100 };
  if (type === "oval") return { w: 120, h: 80 };
  if (type === "image") return { w: 200, h: 200 };
  if (type === "text") {
    ctx.font = "24px Arial";
    return { w: ctx.measureText(text).width, h: 32, fontSize: 24 };
  }
  return { w: 120, h: 80 };
};

const CanvasEditor = ({ initialData }) => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const { id: designId } = useParams();
  const navigate = useNavigate();
  const [saveMessage, setSaveMessage] = useState("");
  const [designName, setDesignName] = useState("");
  const [selectedShape, setSelectedShape] = useState("rectangle");
  const [shapeColor, setShapeColor] = useState("#4f46e5");
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(20);
  const [textColor, setTextColor] = useState("#111827");
  const [pencilColor, setPencilColor] = useState("#333");
  const [brushSize, setBrushSize] = useState(2);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [resizingHandle, setResizingHandle] = useState(null);
  const drawingPointsRef = useRef([]);

  const [history, setHistory] = useState([{ elements: [], lines: [], title: "" }]);
  const [historyIndex, setHistoryIndex] = useState(0);
  const [imageCache, setImageCache] = useState({});

  const { elements, lines, title } = history[historyIndex];

  useEffect(() => {
    setHistory([
      {
        elements: initialData?.jsonData?.elements || [],
        lines: initialData?.jsonData?.lines || [],
        title: initialData?.title || "",
      },
    ]);
    setHistoryIndex(0);
    setDesignName(initialData?.title || "");
  }, [initialData]);

  useEffect(() => {
    elements.forEach((el) => {
      if (el.type === "image" && el.src && !imageCache[el.id]) {
        const img = new window.Image();
        img.src = el.src;
        img.onload = () => setImageCache((c) => ({ ...c, [el.id]: img }));
      }
    });
  }, [elements, imageCache]);

  useEffect(() => {
    if (!canvasRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineCap = "round";
    ctx.lineWidth = 2;

    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w,
        h = el.h;
      const cx = el.x + w / 2;
      const cy = el.y + h / 2;
      ctx.translate(cx, cy);
      ctx.rotate(((el.rotation || 0) * Math.PI) / 180);
      ctx.translate(-cx, -cy);

      if (el.type === "image") {
        const img = imageCache[el.id];
        if (img) {
          ctx.drawImage(img, el.x, el.y, w, h);
          if (idx === selectedIdx) {
            ctx.strokeStyle = "#f59e42";
            ctx.lineWidth = 2;
            ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
            drawHandles(ctx, el.x, el.y, w, h);
          }
        }
      } else if (el.type === "text") {
        ctx.font = `${el.fontSize || 24}px ${el.fontFamily || "Arial"}`;
        ctx.fillStyle = el.color;
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
        const boxW = el.w;
        const boxH = el.h || (el.fontSize || 24) + 8;
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42";
          ctx.strokeRect(el.x - 2, el.y - 2, boxW + 4, boxH + 4);
          drawHandles(ctx, el.x, el.y, boxW, boxH);
        }
      } else {
        ctx.fillStyle = el.color;
        ctx.strokeStyle = el.color;
        switch (el.type) {
          case "rectangle":
          case "square":
            ctx.fillRect(el.x, el.y, w, h);
            break;
          case "circle":
            ctx.beginPath();
            ctx.arc(el.x + w / 2, el.y + h / 2, w / 2, 0, 2 * Math.PI);
            ctx.fill();
            break;
          case "oval":
            ctx.save();
            ctx.translate(el.x + w / 2, el.y + h / 2);
            ctx.scale(1.5, 1);
            ctx.beginPath();
            ctx.arc(0, 0, h / 2, 0, 2 * Math.PI);
            ctx.restore();
            ctx.fill();
            break;
          case "triangle":
            ctx.beginPath();
            ctx.moveTo(el.x, el.y + h);
            ctx.lineTo(el.x + w, el.y + h);
            ctx.lineTo(el.x + w / 2, el.y);
            ctx.closePath();
            ctx.fill();
            break;
          case "parallelogram":
            ctx.beginPath();
            ctx.moveTo(el.x + 20, el.y + h);
            ctx.lineTo(el.x + w + 20, el.y + h);
            ctx.lineTo(el.x + w, el.y);
            ctx.lineTo(el.x, el.y);
            ctx.closePath();
            ctx.fill();
            break;
          case "rhombus":
            ctx.beginPath();
            ctx.moveTo(el.x + w / 2, el.y);
            ctx.lineTo(el.x + w, el.y + h / 2);
            ctx.lineTo(el.x + w / 2, el.y + h);
            ctx.lineTo(el.x, el.y + h / 2);
            ctx.closePath();
            ctx.fill();
            break;
          case "trapezoid":
            ctx.beginPath();
            ctx.moveTo(el.x + 20, el.y + h);
            ctx.lineTo(el.x + w - 20, el.y + h);
            ctx.lineTo(el.x + w, el.y);
            ctx.lineTo(el.x, el.y);
            ctx.closePath();
            ctx.fill();
            break;
          case "kite":
            ctx.beginPath();
            ctx.moveTo(el.x + w / 2, el.y);
            ctx.lineTo(el.x + w, el.y + h / 2);
            ctx.lineTo(el.x + w / 2, el.y + h);
            ctx.lineTo(el.x, el.y + h / 2);
            ctx.closePath();
            ctx.fill();
            break;
          case "pentagon":
          case "hexagon":
          case "heptagon":
          case "octagon":
          case "nonagon":
          case "decagon":
            const sides = {
              pentagon: 5,
              hexagon: 6,
              heptagon: 7,
              octagon: 8,
              nonagon: 9,
              decagon: 10,
            }[el.type];
            drawPolygon(ctx, el.x + w / 2, el.y + h / 2, Math.min(w, h) / 2, sides, el.color);
            break;
          default:
            break;
        }
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42";
          ctx.lineWidth = 2;
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h);
        }
      }
      ctx.restore();
    });

    lines.forEach((line) => {
      ctx.beginPath();
      ctx.strokeStyle = line.mode === "eraser" ? "rgba(0,0,0,1)" : line.color || "#333";
      ctx.lineWidth = line.size || 2;

      ctx.globalCompositeOperation = line.mode === "eraser" ? "destination-out" : "source-over";
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.globalCompositeOperation = "source-over";
    });
  }, [elements, lines, selectedIdx, imageCache]);

  const drawHandles = (ctx, x, y, w, h) => {
    const corners = [
      [x, y],
      [x + w / 2 - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE, y],
      [x + w - HANDLE_SIZE, y + h - HANDLE_SIZE],
      [x, y + h - HANDLE_SIZE],
    ];
    ctx.fillStyle = "#f59e42";
    corners.forEach(([cx, cy]) => ctx.fillRect(cx, cy, HANDLE_SIZE, HANDLE_SIZE));
  };

  const handleCanvasMouseDown = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    if (drawMode) {
      setIsDrawing(true);
      drawingPointsRef.current = [[x, y]];
    } else {
      let clickedIdx = null;
      for (let i = elements.length - 1; i >= 0; i--) {
        const el = elements[i];
        if (x >= el.x && x <= el.x + el.w && y >= el.y && y <= el.y + el.h) {
          clickedIdx = i;
          setDragOffset({ x: x - el.x, y: y - el.y });
          setIsDragging(true);
          break;
        }
      }
      setSelectedIdx(clickedIdx);
    }
  };

  const handleCanvasMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    if (isDrawing) {
      drawingPointsRef.current.push([x, y]);
      setHistory((prev) => {
        const newLines = [...prev[historyIndex].lines];
        newLines.push({
          mode: "pencil",
          color: pencilColor,
          size: brushSize,
          points: [...drawingPointsRef.current],
        });
        const newHistory = [...prev];
        newHistory[historyIndex].lines = newLines;
        return newHistory;
      });
    }

    if (isDragging && selectedIdx !== null) {
      setHistory((prev) => {
        const newHistory = [...prev];
        const el = { ...newHistory[historyIndex].elements[selectedIdx] };
        el.x = x - dragOffset.x;
        el.y = y - dragOffset.y;
        newHistory[historyIndex].elements[selectedIdx] = el;
        return newHistory;
      });
    }
  };

  const handleCanvasMouseUp = () => {
    setIsDragging(false);
    setIsDrawing(false);
  };

  return (
    <div className="flex flex-col lg:flex-row gap-4 p-4 justify-center items-start">
      {/* Sidebar */}
      <aside className="flex-shrink-0 flex flex-col gap-6 p-4 bg-gradient-to-b from-slate-800/60 to-slate-900/40 border border-slate-800 rounded-2xl w-full lg:w-80 shadow-2xl">
        <input
          type="text"
          placeholder="Design name"
          className="px-3 py-2 rounded border border-gray-600"
          value={designName}
          onChange={(e) => setDesignName(e.target.value)}
        />
        <select
          value={selectedShape}
          onChange={(e) => setSelectedShape(e.target.value)}
          className="px-3 py-2 rounded border border-gray-600"
        >
          <option value="rectangle">Rectangle</option>
          <option value="circle">Circle</option>
          <option value="oval">Oval</option>
          <option value="triangle">Triangle</option>
          <option value="square">Square</option>
          <option value="pentagon">Pentagon</option>
          <option value="hexagon">Hexagon</option>
        </select>
        <input
          type="color"
          value={shapeColor}
          onChange={(e) => setShapeColor(e.target.value)}
          className="w-full h-10 p-1 rounded border border-gray-600"
        />
      </aside>

      {/* Main canvas */}
      <main className="flex-1 flex justify-center items-start mt-4 lg:mt-0">
        <motion.div
          initial={{ scale: 0.995, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ type: "spring", stiffness: 120 }}
          className="bg-gradient-to-b from-white/5 to-white/2 p-6 rounded-3xl shadow-2xl border border-white/5"
        >
          <div className="w-[1000px] h-[700px] rounded-xl overflow-hidden relative">
            <canvas
              ref={canvasRef}
              width={1000}
              height={700}
              style={{
                background: "linear-gradient(180deg, #ffffff 0%, #f8fafc 100%)",
                border: "2px solid rgba(80,80,80,0.2)",
              }}
              onMouseDown={handleCanvasMouseDown}
              onMouseMove={handleCanvasMouseMove}
              onMouseUp={handleCanvasMouseUp}
              onMouseLeave={handleCanvasMouseUp}
            />
            {/* HUD overlays */}
            <div className="absolute left-4 top-4 bg-black/30 text-white px-3 py-1 rounded backdrop-blur-sm text-xs">
              Pro Canvas • {elements.length} elements
            </div>
            <div className="absolute right-4 bottom-4 bg-black/20 text-white px-3 py-1 rounded text-xs">
              Status: {saveMessage ? saveMessage : "Unsaved"}
            </div>
          </div>
        </motion.div>
      </main>
    </div>
  );
};

export default CanvasEditor;
