// src/api/api.js
// API Base URL - Change this to your backend URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// JWT Token Management (In-Memory Storage)
let authToken = null;
let currentUser = null;

// Token Manager
export const tokenManager = {
  setToken: (token) => {
    authToken = token;
  },
  
  getToken: () => {
    return authToken;
  },
  
  removeToken: () => {
    authToken = null;
  },
  
  setUser: (user) => {
    currentUser = user;
  },
  
  getUser: () => {
    return currentUser;
  },
  
  removeUser: () => {
    currentUser = null;
  },
  
  clearAuth: () => {
    authToken = null;
    currentUser = null;
  }
};

// Base API Request Function
const apiRequest = async (endpoint, options = {}) => {
  const token = tokenManager.getToken();
  
  const config = {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...(token && { Authorization: `Bearer ${token}` }),
      ...options.headers,
    },
  };

  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, config);
    
    // Handle 401 Unauthorized (Token expired)
    if (response.status === 401) {
      tokenManager.clearAuth();
      window.location.href = '/login';
      throw new Error('Session expired. Please login again.');
    }
    
    // Parse response
    const data = await response.json();
    
    // Handle non-OK responses
    if (!response.ok) {
      throw new Error(data.message || 'Request failed');
    }
    
    return data;
  } catch (error) {
    console.error('API Request Error:', error);
    throw error;
  }
};

// Authentication API
export const authAPI = {
  // Register new user
  register: async (userData) => {
    const response = await apiRequest('/auth/register', {
      method: 'POST',
      body: JSON.stringify(userData),
    });
    
    // Store token and user if returned
    if (response.token) {
      tokenManager.setToken(response.token);
      tokenManager.setUser(response.user);
    }
    
    return response;
  },

  // Login user
  login: async (credentials) => {
    const response = await apiRequest('/auth/login', {
      method: 'POST',
      body: JSON.stringify(credentials),
    });
    
    // Store token and user
    if (response.token) {
      tokenManager.setToken(response.token);
      tokenManager.setUser(response.user);
    }
    
    return response;
  },

  // Logout user
  logout: () => {
    tokenManager.clearAuth();
  },

  // Verify JWT token
  verifyToken: async () => {
    return await apiRequest('/auth/verify', {
      method: 'GET',
    });
  },

  // Google OAuth
  googleAuth: async (googleToken) => {
    const response = await apiRequest('/auth/google', {
      method: 'POST',
      body: JSON.stringify({ token: googleToken }),
    });
    
    if (response.token) {
      tokenManager.setToken(response.token);
      tokenManager.setUser(response.user);
    }
    
    return response;
  },

  // Forgot Password
  forgotPassword: async (email) => {
    return await apiRequest('/auth/forgot-password', {
      method: 'POST',
      body: JSON.stringify({ email }),
    });
  },

  // Reset Password
  resetPassword: async (token, newPassword) => {
    return await apiRequest('/auth/reset-password', {
      method: 'POST',
      body: JSON.stringify({ token, password: newPassword }),
    });
  },
};

// Design API (Protected Routes)
export const designAPI = {
  // Get all designs for current user
  getAllDesigns: async () => {
    return await apiRequest('/designs', {
      method: 'GET',
    });
  },

  // Get single design by ID
  getDesignById: async (id) => {
    return await apiRequest(`/designs/${id}`, {
      method: 'GET',
    });
  },

  // Create new design
  createDesign: async (designData) => {
    return await apiRequest('/designs', {
      method: 'POST',
      body: JSON.stringify(designData),
    });
  },

  // Update design
  updateDesign: async (id, designData) => {
    return await apiRequest(`/designs/${id}`, {
      method: 'PUT',
      body: JSON.stringify(designData),
    });
  },

  // Delete design
  deleteDesign: async (id) => {
    return await apiRequest(`/designs/${id}`, {
      method: 'DELETE',
    });
  },
};

// User API (Protected Routes)
export const userAPI = {
  // Get current user profile
  getProfile: async () => {
    return await apiRequest('/users/profile', {
      method: 'GET',
    });
  },

  // Update user profile
  updateProfile: async (userData) => {
    return await apiRequest('/users/profile', {
      method: 'PUT',
      body: JSON.stringify(userData),
    });
  },

  // Change password
  changePassword: async (passwordData) => {
    return await apiRequest('/users/change-password', {
      method: 'POST',
      body: JSON.stringify(passwordData),
    });
  },
};

// Upload API
export const uploadAPI = {
  // Upload image
  uploadImage: async (file) => {
    const formData = new FormData();
    formData.append('image', file);
    
    const token = tokenManager.getToken();
    
    const response = await fetch(`${API_BASE_URL}/uploads/image`, {
      method: 'POST',
      headers: {
        ...(token && { Authorization: `Bearer ${token}` }),
      },
      body: formData,
    });
    
    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || 'Upload failed');
    }
    
    return response.json();
  },
};

// Export default API object
export default {
  auth: authAPI,
  design: designAPI,
  user: userAPI,
  upload: uploadAPI,
  tokenManager,
};
