// src/pages/Reviews.jsx
import React from 'react';

const Reviews = () => {
  // Example imaginary reviews
  const reviews = [
    { id: 1, name: 'Alice', rating: 5, comment: 'Great experience!' },
    { id: 2, name: 'Bob', rating: 4, comment: 'Really helpful service.' },
    { id: 3, name: 'Charlie', rating: 5, comment: 'Loved it!' },
    { id: 4, name: 'Dana', rating: 3, comment: 'It was okay, could be better.' },
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <h1 className="text-3xl font-bold mb-6">User Reviews</h1>
      <div className="space-y-4">
        {reviews.map(review => (
          <div key={review.id} className="bg-gray-800 p-4 rounded-lg shadow-md">
            <div className="flex justify-between items-center mb-2">
              <h2 className="font-semibold">{review.name}</h2>
              <span className="text-yellow-400">{'⭐'.repeat(review.rating)}</span>
            </div>
            <p className="text-gray-300">{review.comment}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reviews;
