import React, { useRef, useEffect, useState } from 'react';

const HANDLE_SIZE = 16;
const ROTATE_HANDLE_DIST = 30;
const ROTATE_HANDLE_RADIUS = 8;
let nextId = 1;

function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const getDefaultSize = (type, text, ctx) => {
  if (type === 'rectangle') return { w: 120, h: 80 };
  if (type === 'square' || type === 'circle') return { w: 100, h: 100 };
  if (type === 'oval') return { w: 120, h: 80 };
  if (type === 'image') return { w: 200, h: 200 };
  if (type === 'text') {
    ctx.font = '24px Arial';
    return { w: ctx.measureText(text).width, h: 32, fontSize: 24 };
  }
  return { w: 120, h: 80 };
};

const initialState = { elements: [], lines: [] };

const CanvasEditor = () => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);

  const [elements, setElements] = useState(initialState.elements);
  const [lines, setLines] = useState(initialState.lines);
  const [history, setHistory] = useState([initialState]);
  const [historyIndex, setHistoryIndex] = useState(0);
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [resizingHandle, setResizingHandle] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(20);
  const [brushSize, setBrushSize] = useState(2);
  const [pencilColor, setPencilColor] = useState('#333');
  const [textColor, setTextColor] = useState('#111827');
  const [shapeColor, setShapeColor] = useState('#4f46e5');
  const [selectedShape, setSelectedShape] = useState('rectangle');
  const [isRotating, setIsRotating] = useState(false);
  const drawingPointsRef = useRef([]);
  const [imageCache, setImageCache] = useState({});

  useEffect(() => {
    if (historyIndex >= 0 && historyIndex < history.length) {
      setElements(history[historyIndex].elements);
      setLines(history[historyIndex].lines);
    }
  }, [historyIndex, history]);

  useEffect(() => {
    elements.forEach(el => {
      if (el.type === 'image' && el.src && !imageCache[el.id]) {
        const img = new window.Image();
        img.src = el.src;
        img.onload = () => setImageCache(c => ({ ...c, [el.id]: img }));
      }
    });
  }, [elements, imageCache]);

  const saveState = (newElements, newLines) => {
    const newHistory = history.slice(0, historyIndex + 1);
    newHistory.push({
      elements: JSON.parse(JSON.stringify(newElements)),
      lines: JSON.parse(JSON.stringify(newLines)),
    });
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
  };

  function drawShape(ctx, el) {
    const w = el.w, h = el.h;
    switch (el.type) {
      case 'rectangle':
      case 'square':
        ctx.fillRect(el.x, el.y, w, h);
        break;
      case 'circle':
        ctx.beginPath();
        ctx.arc(el.x + w / 2, el.y + h / 2, w / 2, 0, 2 * Math.PI);
        ctx.fill();
        break;
      case 'oval':
        ctx.save();
        ctx.translate(el.x + w / 2, el.y + h / 2);
        ctx.scale(1.5, 1);
        ctx.beginPath();
        ctx.arc(0, 0, h / 2, 0, 2 * Math.PI);
        ctx.restore();
        ctx.fill();
        break;
      case 'triangle':
        ctx.beginPath();
        ctx.moveTo(el.x, el.y + h);
        ctx.lineTo(el.x + w, el.y + h);
        ctx.lineTo(el.x + w / 2, el.y);
        ctx.closePath();
        ctx.fill();
        break;
      case 'parallelogram':
        ctx.beginPath();
        ctx.moveTo(el.x + 20, el.y + h);
        ctx.lineTo(el.x + w + 20, el.y + h);
        ctx.lineTo(el.x + w, el.y);
        ctx.lineTo(el.x, el.y);
        ctx.closePath();
        ctx.fill();
        break;
      case 'rhombus':
        ctx.beginPath();
        ctx.moveTo(el.x + w / 2, el.y);
        ctx.lineTo(el.x + w, el.y + h / 2);
        ctx.lineTo(el.x + w / 2, el.y + h);
        ctx.lineTo(el.x, el.y + h / 2);
        ctx.closePath();
        ctx.fill();
        break;
      case 'trapezoid':
        ctx.beginPath();
        ctx.moveTo(el.x + 20, el.y + h);
        ctx.lineTo(el.x + w - 20, el.y + h);
        ctx.lineTo(el.x + w, el.y);
        ctx.lineTo(el.x, el.y);
        ctx.closePath();
        ctx.fill();
        break;
      case 'kite':
        ctx.beginPath();
        ctx.moveTo(el.x + w / 2, el.y);
        ctx.lineTo(el.x + w, el.y + h / 2);
        ctx.lineTo(el.x + w / 2, el.y + h);
        ctx.lineTo(el.x, el.y + h / 2);
        ctx.closePath();
        ctx.fill();
        break;
      case 'pentagon':
      case 'hexagon':
      case 'heptagon':
      case 'octagon':
      case 'nonagon':
      case 'decagon':
        const sides = { pentagon: 5, hexagon: 6, heptagon: 7, octagon: 8, nonagon: 9, decagon: 10 }[el.type];
        drawPolygon(ctx, el.x + w / 2, el.y + h / 2, Math.min(w, h) / 2, sides, el.color);
        break;
      default:
        break;
    }
  }

  function drawHandles(ctx, x, y, w, h, rotation = 0) {
    const handles = [
      [x - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
    ];
    handles.forEach(([hx, hy]) => {
      ctx.fillStyle = '#fff';
      ctx.strokeStyle = '#333';
      ctx.lineWidth = 1;
      ctx.fillRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
      ctx.strokeRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
    });

    // rotation handle circle above top mid
    const centerX = x + w / 2;
    const centerY = y;
    ctx.save();
    ctx.translate(centerX, centerY);
    ctx.rotate(rotation * Math.PI / 180);
    ctx.beginPath();
    ctx.arc(0, -ROTATE_HANDLE_DIST, ROTATE_HANDLE_RADIUS, 0, 2 * Math.PI);
    ctx.fillStyle = '#f59e42';
    ctx.fill();
    ctx.strokeStyle = '#333';
    ctx.stroke();
    ctx.restore();
  }

  function isPointInRotateHandle(el, mouseX, mouseY) {
    const centerX = el.x + el.w / 2;
    const centerY = el.y;
    const rotation = el.rotation || 0;
    const rad = (rotation * Math.PI) / 180;
    const handleX = centerX + Math.sin(rad) * ROTATE_HANDLE_DIST;
    const handleY = centerY - Math.cos(rad) * ROTATE_HANDLE_DIST;
    const dx = mouseX - handleX;
    const dy = mouseY - handleY;
    return dx * dx + dy * dy <= ROTATE_HANDLE_RADIUS * ROTATE_HANDLE_RADIUS;
  }

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineCap = 'round';
    ctx.lineWidth = 2;
    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w,
        h = el.h;
      const cx = el.x + w / 2;
      const cy = el.y + h / 2;
      const rotationRad = ((el.rotation || 0) * Math.PI) / 180;
      ctx.translate(cx, cy);
      ctx.rotate(rotationRad);
      ctx.translate(-cx, -cy);
      if (el.type === 'image') {
        const img = imageCache[el.id];
        if (img) {
          ctx.drawImage(img, el.x, el.y, w, h);
          if (idx === selectedIdx) {
            ctx.strokeStyle = '#f59e42';
            ctx.lineWidth = 2;
            ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
            drawHandles(ctx, el.x, el.y, w, h, el.rotation);
          }
        }
      } else if (el.type === 'text') {
        ctx.font = `${el.fontSize || 24}px Arial`;
        ctx.fillStyle = el.color;
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
        if (idx === selectedIdx) {
          ctx.strokeStyle = '#f59e42';
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h, el.rotation);
        }
      } else {
        ctx.fillStyle = el.color;
        ctx.strokeStyle = el.color;
        drawShape(ctx, el);
        if (idx === selectedIdx) {
          ctx.strokeStyle = '#f59e42';
          ctx.lineWidth = 2;
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h, el.rotation);
        }
      }
      ctx.restore();
    });
    lines.forEach((line) => {
      ctx.beginPath();
      ctx.strokeStyle = line.mode === 'eraser' ? 'rgba(0,0,0,1)' : line.color || '#333';
      ctx.lineWidth = line.size || 2;
      ctx.globalCompositeOperation = line.mode === 'eraser' ? 'destination-out' : 'source-over';
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.globalCompositeOperation = 'source-over';
    });
  }, [elements, lines, imageCache, selectedIdx]);

  function handleUndo() {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      setSelectedIdx(null);
    }
  }

  function handleRedo() {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      setSelectedIdx(null);
    }
  }

  function getHandleAt(el, mouseX, mouseY) {
    const w = el.w,
      h = el.h;
    const handles = [
      [el.x - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
    ];
    for (let i = 0; i < handles.length; i++) {
      const [hx, hy] = handles[i];
      if (
        mouseX >= hx &&
        mouseX <= hx + HANDLE_SIZE &&
        mouseY >= hy &&
        mouseY <= hy + HANDLE_SIZE
      ) {
        return i;
      }
    }
    return null;
  }

  const clearCanvas = () => {
    saveState([], []);
    setSelectedIdx(null);
  };

  const handleCanvasMouseDown = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    let clicked = false;
    for (let i = elements.length - 1; i >= 0; i--) {
      const el = elements[i];
      if (i === selectedIdx && isPointInRotateHandle(el, mouseX, mouseY)) {
        setIsRotating(true);
        clicked = true;
        break;
      }
      const w = el.w,
        h = el.h;
      const handleIdx = getHandleAt(el, mouseX, mouseY);
      if (i === selectedIdx && handleIdx !== null) {
        setResizingHandle(handleIdx);
        clicked = true;
        break;
      }
      if (
        mouseX >= el.x &&
        mouseX <= el.x + w &&
        mouseY >= el.y &&
        mouseY <= el.y + h
      ) {
        setSelectedIdx(i);
        setDragOffset({ x: mouseX - el.x, y: mouseY - el.y });
        setIsDragging(true);
        clicked = true;
        break;
      }
    }
    if (!clicked) setSelectedIdx(null);

    if (drawMode || eraserMode) {
      setIsDrawing(true);
      drawingPointsRef.current = [[mouseX, mouseY]];
    }
  };

  const handleCanvasMouseMove = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;

    if (isRotating && selectedIdx !== null) {
      const el = elements[selectedIdx];
      const cx = el.x + el.w / 2;
      const cy = el.y + el.h / 2;
      const angleRad = Math.atan2(mouseY - cy, mouseX - cx);
      const angleDeg = (angleRad * 180) / Math.PI + 90;
      const normalizedRotation = (angleDeg + 360) % 360;

      const newElements = elements.map((s, idx) =>
        idx === selectedIdx ? { ...s, rotation: normalizedRotation } : s
      );

      setElements(newElements);
      setHistory(h => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
      return;
    }

    if (resizingHandle !== null && selectedIdx !== null) {
      const el = elements[selectedIdx];
      let newEl = { ...el };
      if (resizingHandle === 0) {
        newEl.x = mouseX;
        newEl.y = mouseY;
        newEl.w = el.w + (el.x - mouseX);
        newEl.h = el.h + (el.y - mouseY);
      } else if (resizingHandle === 1) {
        newEl.y = mouseY;
        newEl.w = mouseX - el.x;
        newEl.h = el.h + (el.y - mouseY);
      } else if (resizingHandle === 2) {
        newEl.x = mouseX;
        newEl.w = el.w + (el.x - mouseX);
        newEl.h = mouseY - el.y;
      } else if (resizingHandle === 3) {
        newEl.w = mouseX - el.x;
        newEl.h = mouseY - el.y;
      }
      newEl.w = Math.max(20, newEl.w);
      newEl.h = Math.max(20, newEl.h);
      if (el.type === 'text') {
        newEl.fontSize = Math.max(12, newEl.h - 8);
      }
      const newElements = elements.map((s, idx) =>
        idx === selectedIdx ? newEl : s
      );
      setElements(newElements);
      setHistory(h => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
      return;
    }

    if (isDragging && selectedIdx !== null) {
      const el = elements[selectedIdx];
      const newElements = elements.map((s, idx) =>
        idx === selectedIdx ? { ...s, x: mouseX - dragOffset.x, y: mouseY - dragOffset.y } : s
      );
      setElements(newElements);
      setHistory(h => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { elements: newElements, lines };
        return newHistory;
      });
    }

    if (isDrawing && (drawMode || eraserMode)) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      const points = drawingPointsRef.current;
      points.push([mouseX, mouseY]);
      ctx.beginPath();
      ctx.moveTo(points[points.length - 2][0], points[points.length - 2][1]);
      ctx.lineTo(mouseX, mouseY);
      ctx.lineWidth = eraserMode ? eraserSize : brushSize;
      ctx.strokeStyle = eraserMode ? 'rgba(0,0,0,1)' : pencilColor;
      ctx.globalCompositeOperation = eraserMode ? 'destination-out' : 'source-over';
      ctx.stroke();
      ctx.globalCompositeOperation = 'source-over';
    }
  };


  const handleCanvasMouseUp = () => {
  if (resizingHandle !== null) {
   setResizingHandle(null);
   saveState(elements, lines);
   return;
  }
  if (isDragging && selectedIdx !== null) {
   saveState(elements, lines);
   setIsDragging(false);
  }
  if (isDrawing) {
   setIsDrawing(false);
  }
  if (isRotating) {
   setIsRotating(false);
   saveState(elements, lines);
   return;
  }
 };


 return (
  <div className="flex flex-col h-screen">
   <header className="flex justify-between items-center p-4 bg-gray-100 border-b">
    <div className="text-xl font-bold">MATTY</div>
    <div className="flex gap-2">{/* Social media icons */}</div>
    <button className="px-4 py-2">About Us</button>
   </header>
   <div className="flex gap-4 p-2 bg-gray-50 border-b">
    <button onClick={clearCanvas}>Clear All</button>
    <button onClick={handleUndo} disabled={historyIndex === 0}>Undo</button>
    <button onClick={handleRedo} disabled={historyIndex === history.length - 1}>Redo</button>
    <button onClick={() => moveElement('up')} disabled={selectedIdx === null || selectedIdx >= elements.length - 1}>Move Up</button>
    <button onClick={() => moveElement('down')} disabled={selectedIdx === null || selectedIdx <= 0}>Move Down</button>
   </div>
   <div className="flex flex-1">
    <aside className="flex flex-col gap-6 p-4 bg-gray-50 border-r min-w-[240px]">
     {/* Drawing Tools */}
<div className="bg-white rounded-lg shadow-sm p-4 mb-2">
 <h3 className="font-semibold text-lg mb-3 border-b pb-2">Drawing Tools</h3>
 <div className="flex gap-2 mb-3">
  <button 
   className={`py-2 px-4 rounded ${drawMode && !eraserMode ? 'bg-blue-100 font-bold' : 'bg-gray-100'}`}
   onClick={() => {
    if (drawMode) setDrawMode(false);
    else {
     setDrawMode(true);
     setEraserMode(false);
    }
   }}
  >
   Pencil
  </button>
  <button 
   className={`py-2 px-4 rounded ${eraserMode ? 'bg-blue-100 font-bold' : 'bg-gray-100'}`}
   onClick={() => {
    if (eraserMode) setEraserMode(false);
    else {
     setEraserMode(true);
     setDrawMode(false);
    }
   }}
  >
   Eraser
  </button>
 </div>
 <div className="mb-3">
  <label className="block text-sm mb-1">Pencil Color</label>
  <input type="color" value={pencilColor} onChange={e => setPencilColor(e.target.value)} className="w-8 h-8 p-0 border-0" />
 </div>
 <div className="mb-3">
  <label className="block text-sm mb-1">Brush Size</label>
  <input type="range" min={1} max={30} value={brushSize} onChange={e => setBrushSize(Number(e.target.value))} className="w-full" />
  <span className="text-xs">{brushSize}px</span>
 </div>
 <div>
  <label className="block text-sm mb-1">Eraser Size</label>
  <input type="range" min={5} max={50} value={eraserSize} onChange={e => setEraserSize(Number(e.target.value))} className="w-full" />
  <span className="text-xs">{eraserSize}px</span>
 </div>
</div>


     {/* Shape Tools */}
     <div className="bg-white rounded-lg shadow-sm p-4 mb-2">
      <h3 className="font-semibold text-lg mb-3 border-b pb-2">Shapes</h3>
      <label className="block text-sm mb-1">Shape</label>
      <select value={selectedShape} onChange={e => { setSelectedShape(e.target.value); setDrawMode(false); setEraserMode(false); }} className="mb-3 p-1 border rounded w-full">
       <option value="rectangle">Rectangle</option>
       <option value="square">Square</option>
       <option value="circle">Circle</option>
       <option value="oval">Oval</option>
       <option value="triangle">Triangle</option>
       <option value="parallelogram">Parallelogram</option>
       <option value="rhombus">Rhombus</option>
       <option value="trapezoid">Trapezoid</option>
       <option value="kite">Kite</option>
       <option value="pentagon">Pentagon</option>
       <option value="hexagon">Hexagon</option>
       <option value="heptagon">Heptagon</option>
       <option value="octagon">Octagon</option>
       <option value="nonagon">Nonagon</option>
       <option value="decagon">Decagon</option>
      </select>
      <label className="block text-sm mb-1">Shape Color</label>
      <input type="color" value={shapeColor} onChange={e => {
       setShapeColor(e.target.value);
       if (selectedIdx !== null) {
        const newElements = elements.map((el, idx) =>
         idx === selectedIdx ? { ...el, color: e.target.value } : el
        );
        saveState(newElements, lines);
       }
      }} className="w-8 h-8 p-0 border-0" />
      <button className="w-full mt-3 py-2 rounded bg-gray-100 hover:bg-blue-50" onClick={handleAddShape}>Add Shape</button>
     </div>
     {/* Text Tools */}
     <div className="bg-white rounded-lg shadow-sm p-4 mb-2">
      <h3 className="font-semibold text-lg mb-3 border-b pb-2">Text</h3>
      <label className="block text-sm mb-1">Text Color</label>
      <input type="color" value={textColor} onChange={e => {
       setTextColor(e.target.value);
       if (selectedIdx !== null && elements[selectedIdx]?.type === 'text') {
        const newElements = elements.map((el, idx) =>
         idx === selectedIdx ? { ...el, color: e.target.value } : el
        );
        saveState(newElements, lines);
       }
      }} className="w-8 h-8 p-0 border-0" />
      <button className="w-full mt-3 py-2 rounded bg-gray-100 hover:bg-blue-50" onClick={handleAddText}>Add Text</button>
     </div>
     {/* Image & Download */}
     <div className="bg-white rounded-lg shadow-sm p-4">
      <button className="w-full mb-2 py-2 rounded bg-gray-100 hover:bg-blue-50" onClick={handleUploadImage}>Upload Image</button>
      <button className="w-full py-2 rounded bg-gray-100 hover:bg-blue-50" onClick={handleDownload}>Download</button>
      <input type="file" accept="image/*" ref={fileInputRef} style={{ display: 'none' }} onChange={handleFileChange} />
     </div>
    </aside>
    <main className="flex-1 flex items-center justify-center">
     <canvas
      ref={canvasRef}
      width={1000}
      height={700}
      style={{ background: '#fff', border: '1px solid #eee', marginTop: '-250px' }}
      onMouseDown={handleCanvasMouseDown}
      onMouseMove={handleCanvasMouseMove}
      onMouseUp={handleCanvasMouseUp}
      onMouseLeave={handleCanvasMouseUp}
     />
    </main>
   </div>
  </div>
 );
};


export default CanvasEditor;
