import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [designs, setDesigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchDesigns() {
      try {
        const token = localStorage.getItem('token'); // adjust if stored differently
        const res = await fetch('/api/designs', {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (!res.ok) throw new Error('Failed to fetch designs');
        const data = await res.json();
        setDesigns(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchDesigns();
  }, []);

  const openDesign = (design) => {
    navigate('/editor', { state: { design } });
  };

  if (loading) return <p>Loading designs...</p>;
  if (error) return <p>Error: {error}</p>;
  if (designs.length === 0) return <p>No saved designs found.</p>;

  return (
    <div>
      <h1>Your Saved Designs</h1>
      <div className="design-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit,minmax(200px,1fr))', gap: 10 }}>
        {designs.map(d => (
          <div key={d._id} style={{ border: '1px solid #ccc', padding: 10, borderRadius: 6 }}>
            <h3>{d.title}</h3>
            {d.thumbnailUrl && <img src={d.thumbnailUrl} alt={d.title} style={{ width: '100%', borderRadius: 4 }} />}
            <button onClick={() => openDesign(d)} style={{ marginTop: 5, padding: 6 }}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
