import React, { useRef, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

// Utilities
function dataURLtoBlob(dataurl) {
  var arr = dataurl.split(","), mime = arr[0].match(/:(.*?);/)[1],
    bstr = atob(arr[1]), n = bstr.length, u8arr = new Uint8Array(n);
  while(n--) u8arr[n] = bstr.charCodeAt(n);
  return new Blob([u8arr], {type:mime});
}

function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const HANDLE_SIZE = 16;
let nextId = 1;

// Get default element size
const getDefaultSize = (type, text, ctx) => {
  if (type === "rectangle") return { w: 120, h: 80 };
  if (type === "square" || type === "circle") return { w: 100, h: 100 };
  if (type === "oval") return { w: 120, h: 80 };
  if (type === "image") return { w: 200, h: 200 };
  if (type === "text") {
    ctx.font = "24px Arial";
    return { w: ctx.measureText(text).width, h: 32, fontSize: 24 };
  }
  return { w: 120, h: 80 };
};

// Main Component
const CanvasEditor = ({ initialData }) => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const { id: designId } = useParams();
  const navigate = useNavigate();

  // States
  const [saveMessage, setSaveMessage] = useState("");
  const [designName, setDesignName] = useState("");
  const [selectedShape, setSelectedShape] = useState("rectangle");
  const [shapeColor, setShapeColor] = useState("#4f46e5");
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(20);
  const [textColor, setTextColor] = useState("#111827");
  const [pencilColor, setPencilColor] = useState("#333");
  const [brushSize, setBrushSize] = useState(2);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [resizingHandle, setResizingHandle] = useState(null);
  const drawingPointsRef = useRef([]);
  const [fontFamily, setFontFamily] = useState("Arial");

  // History for undo/redo
  const [history, setHistory] = useState([{ elements: [], lines: [], title: "" }]);
  const [historyIndex, setHistoryIndex] = useState(0);
  const [imageCache, setImageCache] = useState({});

  const { elements, lines, title } = history[historyIndex];

  // Load initial data
  useEffect(() => {
    setHistory([{
      elements: initialData?.jsonData?.elements || [],
      lines: initialData?.jsonData?.lines || [],
      title: initialData?.title || "",
    }]);
    setHistoryIndex(0);
    setDesignName(initialData?.title || "");
  }, [initialData]);

  // Cache images
  useEffect(() => {
    elements.forEach(el => {
      if(el.type === "image" && el.src && !imageCache[el.id]){
        const img = new Image();
        img.src = el.src;
        img.onload = () => setImageCache(c => ({...c, [el.id]: img}));
      }
    });
  }, [elements, imageCache]);

  // Drawing function
  useEffect(() => {
    if(!canvasRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0,0,canvas.width,canvas.height);
    ctx.lineCap = "round";
    ctx.lineWidth = 2;

    // Draw elements
    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w, h = el.h;
      const cx = el.x + w/2, cy = el.y + h/2;
      ctx.translate(cx, cy);
      ctx.rotate((el.rotation || 0) * Math.PI / 180);
      ctx.translate(-cx, -cy);

      if(el.type === "image"){
        const img = imageCache[el.id];
        if(img){
          ctx.drawImage(img, el.x, el.y, w, h);
          if(idx === selectedIdx){
            ctx.strokeStyle = "#f59e42";
            ctx.lineWidth = 2;
            ctx.strokeRect(el.x-2, el.y-2, w+4, h+4);
            drawHandles(ctx, el.x, el.y, w, h);
          }
        }
      } else if(el.type === "text"){
        ctx.font = `${el.fontSize || 24}px ${el.fontFamily || "Arial"}`;
        ctx.fillStyle = el.color;
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
        if(idx === selectedIdx){
          ctx.strokeStyle = "#f59e42";
          ctx.strokeRect(el.x-2, el.y-2, el.w+4, (el.fontSize||24)+8);
          drawHandles(ctx, el.x, el.y, el.w, (el.fontSize||24)+8);
        }
      } else {
        ctx.fillStyle = el.color;
        ctx.strokeStyle = el.color;
        switch(el.type){
          case "rectangle":
          case "square": ctx.fillRect(el.x, el.y, w, h); break;
          case "circle": ctx.beginPath(); ctx.arc(el.x+w/2, el.y+h/2, w/2,0,2*Math.PI); ctx.fill(); break;
          case "oval": ctx.save(); ctx.translate(el.x+w/2, el.y+h/2); ctx.scale(1.5,1); ctx.beginPath(); ctx.arc(0,0,h/2,0,2*Math.PI); ctx.restore(); ctx.fill(); break;
          case "triangle": ctx.beginPath(); ctx.moveTo(el.x, el.y+h); ctx.lineTo(el.x+w, el.y+h); ctx.lineTo(el.x+w/2, el.y); ctx.closePath(); ctx.fill(); break;
          case "parallelogram": ctx.beginPath(); ctx.moveTo(el.x+20, el.y+h); ctx.lineTo(el.x+w+20, el.y+h); ctx.lineTo(el.x+w, el.y); ctx.lineTo(el.x, el.y); ctx.closePath(); ctx.fill(); break;
          case "rhombus": ctx.beginPath(); ctx.moveTo(el.x+w/2, el.y); ctx.lineTo(el.x+w, el.y+h/2); ctx.lineTo(el.x+w/2, el.y+h); ctx.lineTo(el.x, el.y+h/2); ctx.closePath(); ctx.fill(); break;
          case "trapezoid": ctx.beginPath(); ctx.moveTo(el.x+20, el.y+h); ctx.lineTo(el.x+w-20, el.y+h); ctx.lineTo(el.x+w, el.y); ctx.lineTo(el.x, el.y); ctx.closePath(); ctx.fill(); break;
          case "kite": ctx.beginPath(); ctx.moveTo(el.x+w/2, el.y); ctx.lineTo(el.x+w, el.y+h/2); ctx.lineTo(el.x+w/2, el.y+h); ctx.lineTo(el.x, el.y+h/2); ctx.closePath(); ctx.fill(); break;
          default:
            if(["pentagon","hexagon","heptagon","octagon","nonagon","decagon"].includes(el.type)){
              const sides = {pentagon:5, hexagon:6, heptagon:7, octagon:8, nonagon:9, decagon:10}[el.type];
              drawPolygon(ctx, el.x+w/2, el.y+h/2, Math.min(w,h)/2, sides, el.color);
            }
        }
        if(idx === selectedIdx){
          ctx.strokeStyle = "#f59e42";
          ctx.lineWidth = 2;
          ctx.strokeRect(el.x-2, el.y-2, w+4, h+4);
          drawHandles(ctx, el.x, el.y, w, h);
        }
      }
      ctx.restore();
    });

    // Draw lines
    lines.forEach(line=>{
      ctx.beginPath();
      ctx.strokeStyle = line.mode==="eraser"?"rgba(0,0,0,1)":line.color||"#333";
      ctx.lineWidth = line.size||2;
      ctx.globalCompositeOperation = line.mode==="eraser"?"destination-out":"source-over";
      line.points.forEach(([x,y],i)=> i===0?ctx.moveTo(x,y):ctx.lineTo(x,y));
      ctx.stroke();
      ctx.globalCompositeOperation = "source-over";
    });

  }, [elements, lines, imageCache, selectedIdx]);

  function drawHandles(ctx, x, y, w, h){
    const handles = [
      [x-HANDLE_SIZE/2, y-HANDLE_SIZE/2],
      [x+w-HANDLE_SIZE/2, y-HANDLE_SIZE/2],
      [x-HANDLE_SIZE/2, y+h-HANDLE_SIZE/2],
      [x+w-HANDLE_SIZE/2, y+h-HANDLE_SIZE/2]
    ];
    handles.forEach(([hx,hy])=>{
      ctx.fillStyle="#fff";
      ctx.strokeStyle="#333";
      ctx.lineWidth=1;
      ctx.fillRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
      ctx.strokeRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
    });
  }

  // Save history
  const saveState = (newElements, newLines, newTitle = designName) => {
    const newHistory = history.slice(0, historyIndex+1);
    newHistory.push({
      elements: JSON.parse(JSON.stringify(newElements)),
      lines: JSON.parse(JSON.stringify(newLines)),
      title: newTitle
    });
    setHistory(newHistory);
    setHistoryIndex(newHistory.length-1);
    setDesignName(newTitle);
  };

  const handleUndo = () => { if(historyIndex>0){setHistoryIndex(historyIndex-1); setSelectedIdx(null);} };
  const handleRedo = () => { if(historyIndex<history.length-1){setHistoryIndex(historyIndex+1); setSelectedIdx(null);} };

  // Mouse handlers
  const getHandleAt = (el, mouseX, mouseY)=>{
    const handles = [
      [el.x-HANDLE_SIZE/2, el.y-HANDLE_SIZE/2],
      [el.x+el.w-HANDLE_SIZE/2, el.y-HANDLE_SIZE/2],
      [el.x-HANDLE_SIZE/2, el.y+el.h-HANDLE_SIZE/2],
      [el.x+el.w-HANDLE_SIZE/2, el.y+el.h-HANDLE_SIZE/2]
    ];
    for(let i=0;i<handles.length;i++){
      const [hx,hy] = handles[i];
      if(mouseX>=hx && mouseX<=hx+HANDLE_SIZE && mouseY>=hy && mouseY<=hy+HANDLE_SIZE) return i;
    }
    return null;
  };

  const handleCanvasMouseDown = (e)=>{
    if(!canvasRef.current) return;
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    let clicked = false;
    for(let i=elements.length-1;i>=0;i--){
      const el = elements[i];
      const handleIdx = getHandleAt(el, mouseX, mouseY);
      if(i===selectedIdx && handleIdx!==null){setResizingHandle(handleIdx); clicked=true; break;}
      if(mouseX>=el.x && mouseX<=el.x+el.w && mouseY>=el.y && mouseY<=el.y+el.h){
        setSelectedIdx(i);
        setDragOffset({x:mouseX-el.x, y:mouseY-el.y});
        setIsDragging(true);
        clicked=true;
        break;
      }
    }
    if(!clicked) setSelectedIdx(null);
    if(drawMode || eraserMode){ setIsDrawing(true); drawingPointsRef.current=[[mouseX,mouseY]]; }
  };

  const handleCanvasMouseMove = (e)=>{
    if(!canvasRef.current) return;
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;

    if(resizingHandle!==null && selectedIdx!==null){
      const el = elements[selectedIdx];
      const newEl = {...el};
      if(resizingHandle===0){ newEl.x=mouseX; newEl.y=mouseY; newEl.w=el.w+(el.x-mouseX); newEl.h=el.h+(el.y-mouseY);}
      else if(resizingHandle===1){ newEl.y=mouseY; newEl.w=mouseX-el.x; newEl.h=el.h+(el.y-mouseY);}
      else if(resizingHandle===2){ newEl.x=mouseX; newEl.w=el.w+(el.x-mouseX); newEl.h=mouseY-el.y;}
      else if(resizingHandle===3){ newEl.w=mouseX-el.x; newEl.h=mouseY-el.y;}
      newEl.w = Math.max(20, newEl.w);
      newEl.h = Math.max(20, newEl.h);
      if(el.type==="text") newEl.fontSize = Math.max(12,newEl.h-8);
      const newElements = elements.map((s,idx)=> idx===selectedIdx?newEl:s);
      setHistory(h=>{ const nh=h.slice(0,historyIndex+1); nh[historyIndex]={elements:newElements, lines}; return nh; });
      return;
    }

    if(isDragging && selectedIdx!==null){
      const newElements = elements.map((s,idx)=> idx===selectedIdx? {...s,x:mouseX-dragOffset.x,y:mouseY-dragOffset.y}:s );
      setHistory(h=>{ const nh=h.slice(0,historyIndex+1); nh[historyIndex]={elements:newElements, lines}; return nh; });
    }

    if(isDrawing && (drawMode || eraserMode)){
      const ctx = canvasRef.current.getContext("2d");
      const points = drawingPointsRef.current;
      points.push([mouseX,mouseY]);
      ctx.beginPath();
      ctx.moveTo(points[points.length-2][0], points[points.length-2][1]);
      ctx.lineTo(mouseX, mouseY);
      ctx.lineWidth = eraserMode?eraserSize:brushSize;
      ctx.strokeStyle = eraserMode?pencilColor:"#333";
      ctx.globalCompositeOperation = eraserMode?"destination-out":"source-over";
      ctx.stroke();
      ctx.globalCompositeOperation="source-over";
    }
  };

  const handleCanvasMouseUp = ()=>{
    if(isDrawing){
      if(!canvasRef.current) return;
      const newLine={id:nextId++, points:drawingPointsRef.current, color:pencilColor, size:eraserMode?eraserSize:brushSize, mode:eraserMode?"eraser":"draw"};
      saveState(elements,[...lines,newLine],designName);
    }
    if(resizingHandle!==null) setResizingHandle(null);
    if(isDragging) setIsDragging(false);
    setIsDrawing(false);
  };

  // Add Shape
  const handleAddShape = ()=>{
    if(!canvasRef.current) return;
    const ctx = canvasRef.current.getContext("2d");
    const {w,h} = getDefaultSize(selectedShape,"",ctx);
    const newEl={id:nextId++, type:selectedShape, x:300, y:100, color:shapeColor, w, h, rotation:0};
    if(selectedShape!=="image") saveState([...elements,newEl], lines, designName);
  };

  // Add Text
  const handleAddText = ()=>{
    if(!canvasRef.current) return;
    const text = prompt("Enter text:");
    if(!text) return;
    const ctx = canvasRef.current.getContext("2d");
    ctx.font = `24px ${fontFamily}`;
    const {w,h,fontSize} = getDefaultSize("text", text, ctx);
    const newText={id:nextId++, type:"text", text, x:100, y:100, color:textColor, w, h, fontSize, fontFamily, rotation:0};
    saveState([...elements,newText], lines, designName);
  };

  // Color changes
  const handleShapeColorChange = (e)=>{ setShapeColor(e.target.value); if(selectedIdx!==null){ const newElements = elements.map((el,idx)=> idx===selectedIdx? {...el,color:e.target.value}:el); saveState(newElements, lines, designName);} };
  const handleTextColorChange = (e)=>{ setTextColor(e.target.value); if(selectedIdx!==null && elements[selectedIdx]?.type==="text"){ const newElements = elements.map((el,idx)=> idx===selectedIdx? {...el,color:e.target.value}:el); saveState(newElements, lines, designName);} };

  // Image Upload
  const handleUploadImage = ()=>fileInputRef.current.click();
  const handleFileChange = (e)=>{
    const file = e.target.files[0];
    if(!file) return;
    const reader = new FileReader();
    reader.onload = function(event){
      const newEl={id:nextId++, type:"image", x:50, y:50, w:200, h:200, src:event.target.result, rotation:0};
      saveState([...elements,newEl], lines, designName);
    };
    reader.readAsDataURL(file);
    e.target.value="";
  };

  // Export / Clear / Delete / Move
  const handleDownload = ()=>{
    if(!canvasRef.current) return;
    const link=document.createElement("a");
    link.download=designName.trim()||"canvas.png";
    link.href=canvasRef.current.toDataURL();
    link.click();
  };
  const clearCanvas = ()=>{ setHistory([{elements:[],lines:[],title:designName}]); setHistoryIndex(0); setSelectedIdx(null); };
  const handleDeleteSelected = ()=>{ if(selectedIdx!==null){ const newElements = elements.filter((_,idx)=>idx!==selectedIdx); saveState(newElements, lines, designName); setSelectedIdx(null);} };
  const moveElement = (dir)=>{ if(selectedIdx===null) return; const newElements=[...elements]; let idx=selectedIdx; if(dir==="up" && idx<newElements.length-1){ [newElements[idx],newElements[idx+1]]=[newElements[idx+1],newElements[idx]]; setSelectedIdx(idx+1);} if(dir==="down" && idx>0){ [newElements[idx],newElements[idx-1]]=[newElements[idx-1],newElements[idx]]; setSelectedIdx(idx-1);} saveState(newElements, lines, designName); };

  // Cloud Save
  const handleSaveToCloud = async ()=>{
    setSaveMessage("");
    try{
      const token = localStorage.getItem("token");
      const imageDataURL = canvasRef.current.toDataURL("image/png");
      const imageBlob = dataURLtoBlob(imageDataURL);
      const formData = new FormData();
      formData.append("file", imageBlob);
      formData.append("upload_preset", "MattyDesignTool");
      const res = await fetch("https://api.cloudinary.com/v1_1/dvxazjesy/image/upload",{method:"POST", body:formData});
      const data = await res.json();
      const thumbnailUrl = data.secure_url;
      const jsonData = {elements, lines, title:designName};
      let response;
      if(designId){
        response = await fetch(`http://localhost:5000/api/designs/${designId}`,{method:'PUT', headers:{'Content-Type':'application/json', Authorization:`Bearer ${token}`}, body:JSON.stringify({title:designName, thumbnailUrl, jsonData})});
      } else {
        response = await fetch('http://localhost:5000/api/designs',{method:'POST', headers:{'Content-Type':'application/json', Authorization:`Bearer ${token}`}, body:JSON.stringify({title:designName, thumbnailUrl, jsonData})});
      }
      if(!response.ok) throw new Error("Failed to save design!");
      setSaveMessage("✅ Design saved successfully!");
    }catch(err){ setSaveMessage("❌ Save failed: "+err.message);}
  };

  // Animation presets
  const cardAnim = {hidden:{opacity:0,y:8}, show:{opacity:1,y:0}};
  const btnHover = {scale:1.03};
  const btnTap = {scale:0.97};

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-b from-[#0f172a] via-[#0b1220] to-[#071024] text-slate-100">
      {/* HEADER */}
      <header className="flex justify-between items-center p-4 bg-gradient-to-r from-[#0ea5e9] via-[#8b5cf6] to-[#ef4444] shadow-xl">
        <motion.div className="flex items-center gap-3 cursor-pointer select-none"
          onClick={()=>navigate("/dashboard")}
          whileHover={{scale:1.02, rotate:0.5}}
          whileTap={{scale:0.98}}
          initial={{opacity:0,x:-20}}
          animate={{opacity:1,x:0}}
          transition={{type:"spring", stiffness:140}}
        >
          <div className="text-5xl font-extrabold" style={{ fontFamily: '"Kablammo", system-ui', textShadow:"0 6px 20px rgba(0,0,0,0.35)"}}>MATTY</div>
          <div className="text-sm font-medium opacity-90">Design Studio</div>
        </motion.div>
        <div className="flex items-center gap-4">
          <motion.a whileHover={btnHover} whileTap={btnTap} className="text-sm px-3 py-1 rounded-md bg-white/20 backdrop-blur-sm border border-white/30">About us</motion.a>
          <motion.button onClick={handleSaveToCloud} whileHover={btnHover} whileTap={btnTap} className="px-3 py-1 rounded-md bg-white text-slate-900 font-medium shadow">Save</motion.button>
          <motion.button onClick={handleDownload} whileHover={btnHover} whileTap={btnTap} className="px-3 py-1 rounded-md bg-white/10 text-white border border-white/20">Export</motion.button>
        </div>
      </header>

      {/* Canvas Input */}
      <div className="flex flex-grow p-4">
        <canvas ref={canvasRef} width={1200} height={700} 
          className="border border-slate-700 rounded-2xl bg-slate-950 shadow-lg"
          onMouseDown={handleCanvasMouseDown}
          onMouseMove={handleCanvasMouseMove}
          onMouseUp={handleCanvasMouseUp}
        />
        <input type="file" accept="image/*" ref={fileInputRef} onChange={handleFileChange} className="hidden"/>
      </div>
    </div>
  );
};

export default CanvasEditor;
