import React, { useRef, useEffect, useState } from "react";

// Polygon drawing helper
function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const HANDLE_SIZE = 16;
let nextId = 1;

const getDefaultSize = (type, text, ctx) => {
  if (type === "rectangle") return { w: 120, h: 80 };
  if (type === "square" || type === "circle") return { w: 100, h: 100 };
  if (type === "oval") return { w: 120, h: 80 };
  if (type === "image") return { w: 200, h: 200 };
  if (type === "text") {
    ctx.font = "24px Arial";
    return { w: ctx.measureText(text).width, h: 32, fontSize: 24 };
  }
  return { w: 120, h: 80 };
};

const CanvasEditor = ({ initialData }) => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);

  const [designName, setDesignName] = useState(initialData?.title || "");
  const [selectedShape, setSelectedShape] = useState("rectangle");
  const [shapeColor, setShapeColor] = useState("#4f46e5");
  const [drawMode, setDrawMode] = useState(false);
  const [eraserMode, setEraserMode] = useState(false);
  const [eraserSize, setEraserSize] = useState(20);
  const [textColor, setTextColor] = useState("#111827");
  const [pencilColor, setPencilColor] = useState("#333");
  const [brushSize, setBrushSize] = useState(2);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [selectedIdx, setSelectedIdx] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [resizingHandle, setResizingHandle] = useState(null);
  const drawingPointsRef = useRef([]);

  const [history, setHistory] = useState([
    {
      elements: initialData?.jsonData?.elements || [],
      lines: initialData?.jsonData?.lines || [],
      title: initialData?.title || "",
    },
  ]);
  const [historyIndex, setHistoryIndex] = useState(0);
  const [imageCache, setImageCache] = useState({});
  const { elements, lines, title } = history[historyIndex];

  useEffect(() => {
    setDesignName(title || "");
  }, [title]);

  useEffect(() => {
    elements.forEach((el) => {
      if (el.type === "image" && el.src && !imageCache[el.id]) {
        const img = new window.Image();
        img.src = el.src;
        img.onload = () => setImageCache((c) => ({ ...c, [el.id]: img }));
      }
    });
    // Only want to run for new elements or imageCache changes.
    // eslint-disable-next-line
  }, [elements, imageCache]);

  useEffect(() => {
    if (!canvasRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineCap = "round";
    ctx.lineWidth = 2;

    elements.forEach((el, idx) => {
      ctx.save();
      const w = el.w, h = el.h;
      const cx = el.x + w / 2, cy = el.y + h / 2;
      ctx.translate(cx, cy);
      ctx.rotate(((el.rotation || 0) * Math.PI) / 180);
      ctx.translate(-cx, -cy);

      if (el.type === "image") {
        const img = imageCache[el.id];
        if (img) {
          ctx.drawImage(img, el.x, el.y, w, h);
          if (idx === selectedIdx) {
            ctx.strokeStyle = "#f59e42";
            ctx.lineWidth = 2;
            ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
            drawHandles(ctx, el.x, el.y, w, h);
          }
        }
      } else if (el.type === "text") {
        ctx.font = `${el.fontSize || 24}px Arial`;
        ctx.fillStyle = el.color;
        ctx.fillText(el.text, el.x, el.y + (el.fontSize || 24));
        const boxW = el.w;
        const boxH = el.h || (el.fontSize || 24) + 8;
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42";
          ctx.strokeRect(el.x - 2, el.y - 2, boxW + 4, boxH + 4);
          drawHandles(ctx, el.x, el.y, boxW, boxH);
        }
      } else {
        ctx.fillStyle = el.color;
        ctx.strokeStyle = el.color;
        switch (el.type) {
          case "rectangle":
          case "square":
            ctx.fillRect(el.x, el.y, w, h); break;
          case "circle":
            ctx.beginPath();
            ctx.arc(el.x + w / 2, el.y + h / 2, w / 2, 0, 2 * Math.PI);
            ctx.fill(); break;
          case "oval":
            ctx.save(); ctx.translate(el.x + w / 2, el.y + h / 2); ctx.scale(1.5, 1);
            ctx.beginPath(); ctx.arc(0, 0, h / 2, 0, 2 * Math.PI); ctx.restore(); ctx.fill(); break;
          case "triangle":
            ctx.beginPath(); ctx.moveTo(el.x, el.y + h);
            ctx.lineTo(el.x + w, el.y + h);
            ctx.lineTo(el.x + w / 2, el.y);
            ctx.closePath(); ctx.fill(); break;
          case "parallelogram":
            ctx.beginPath(); ctx.moveTo(el.x + 20, el.y + h);
            ctx.lineTo(el.x + w + 20, el.y + h);
            ctx.lineTo(el.x + w, el.y);
            ctx.lineTo(el.x, el.y);
            ctx.closePath(); ctx.fill(); break;
          case "rhombus":
            ctx.beginPath();
            ctx.moveTo(el.x + w / 2, el.y);
            ctx.lineTo(el.x + w, el.y + h / 2);
            ctx.lineTo(el.x + w / 2, el.y + h);
            ctx.lineTo(el.x, el.y + h / 2);
            ctx.closePath(); ctx.fill(); break;
          case "trapezoid":
            ctx.beginPath();
            ctx.moveTo(el.x + 20, el.y + h);
            ctx.lineTo(el.x + w - 20, el.y + h);
            ctx.lineTo(el.x + w, el.y);
            ctx.lineTo(el.x, el.y);
            ctx.closePath(); ctx.fill(); break;
          case "kite":
            ctx.beginPath();
            ctx.moveTo(el.x + w / 2, el.y);
            ctx.lineTo(el.x + w, el.y + h / 2);
            ctx.lineTo(el.x + w / 2, el.y + h);
            ctx.lineTo(el.x, el.y + h / 2); ctx.closePath(); ctx.fill(); break;
          case "pentagon":
          case "hexagon":
          case "heptagon":
          case "octagon":
          case "nonagon":
          case "decagon":
            const sides = {
              pentagon: 5, hexagon: 6, heptagon: 7, octagon: 8,
              nonagon: 9, decagon: 10
            }[el.type];
            drawPolygon(ctx, el.x + w / 2, el.y + h / 2, Math.min(w, h) / 2, sides, el.color); break;
          default: break;
        }
        if (idx === selectedIdx) {
          ctx.strokeStyle = "#f59e42"; ctx.lineWidth = 2;
          ctx.strokeRect(el.x - 2, el.y - 2, w + 4, h + 4);
          drawHandles(ctx, el.x, el.y, w, h);
        }
      }
      ctx.restore();
    });

    lines.forEach((line) => {
      ctx.beginPath();
      ctx.strokeStyle = line.mode === "eraser" ? "rgba(0,0,0,1)" : line.color || "#333";
      ctx.lineWidth = line.size || 2;
      ctx.globalCompositeOperation = line.mode === "eraser" ? "destination-out" : "source-over";
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.globalCompositeOperation = "source-over";
    });
  }, [elements, lines, imageCache, selectedIdx]);

  function drawHandles(ctx, x, y, w, h) {
    const handles = [
      [x - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y - HANDLE_SIZE / 2],
      [x - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
      [x + w - HANDLE_SIZE / 2, y + h - HANDLE_SIZE / 2],
    ];
    handles.forEach(([hx, hy]) => {
      ctx.fillStyle = "#fff";
      ctx.strokeStyle = "#333";
      ctx.lineWidth = 1;
      ctx.fillRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
      ctx.strokeRect(hx, hy, HANDLE_SIZE, HANDLE_SIZE);
    });
  }

  const saveState = (newElements, newLines, newTitle = designName) => {
    const newHistory = history.slice(0, historyIndex + 1);
    newHistory.push({
      elements: JSON.parse(JSON.stringify(newElements)),
      lines: JSON.parse(JSON.stringify(newLines)),
      title: newTitle,
    });
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
    setDesignName(newTitle);
  };

  const handleUndo = () => {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      setSelectedIdx(null);
    }
  };

  const handleRedo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      setSelectedIdx(null);
    }
  };

  const getHandleAt = (el, mouseX, mouseY) => {
    const { w, h } = el;
    const handles = [
      [el.x - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y - HANDLE_SIZE / 2],
      [el.x - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
      [el.x + w - HANDLE_SIZE / 2, el.y + h - HANDLE_SIZE / 2],
    ];
    for (let i = 0; i < handles.length; i++) {
      const [hx, hy] = handles[i];
      if (
        mouseX >= hx &&
        mouseX <= hx + HANDLE_SIZE &&
        mouseY >= hy &&
        mouseY <= hy + HANDLE_SIZE
      )
        return i;
    }
    return null;
  };

  // ...rest of handlers remain exactly as in your code (all unchanged)...
  // (handleCanvasMouseDown, handleCanvasMouseMove, handleCanvasMouseUp, etc.)

  const handleSaveToCloud = async () => {
    if (!canvasRef.current) {
      alert("Canvas is not ready yet!");
      return;
    }
    // Generate the data URL safely only if canvasRef.current exists
    const imageDataURL = canvasRef.current.toDataURL("image/png");
    try {
      const response = await fetch("http://localhost:5000/api/designs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({
          image: imageDataURL,
          title: designName.trim() || "Untitled Design",
          jsonData: { elements, lines }
        })
      });
      const data = await response.json();
      if (response.ok) {
        alert("Design saved and uploaded successfully!");
      } else {
        alert("Failed to upload design: " + (data.message || "Unknown error"));
      }
    } catch (error) {
      alert("Upload failed, please try again.");
    }
  };

  // ...rest of your event handler functions...

  return (
    <div className="flex flex-col h-screen">
      {/* ...header, sidebars... */}
      <main className="flex-1 flex items-center justify-center">
        <canvas
          ref={canvasRef}
          width={1000}
          height={700}
          style={{ background: "#fff", border: "1px solid #eee", marginTop: "-250px" }}
          onMouseDown={handleCanvasMouseDown}
          onMouseMove={handleCanvasMouseMove}
          onMouseUp={handleCanvasMouseUp}
          onMouseLeave={handleCanvasMouseUp}
        />
      </main>
      {/* ...controls, sidebars, modals, etc. */}
    </div>
  );
};

export default CanvasEditor;
