import React, { useRef, useEffect, useState } from 'react';

const CanvasEditor = () => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastPoint, setLastPoint] = useState(null);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.lineWidth = 2;
    ctx.strokeStyle = '#333';
    ctx.lineCap = 'round';
  }, []);

  // Drawing logic
  const startDrawing = (e) => {
    setIsDrawing(true);
    setLastPoint({
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    });
  };

  const draw = (e) => {
    if (!isDrawing) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    ctx.moveTo(lastPoint.x, lastPoint.y);
    ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    ctx.stroke();
    setLastPoint({
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    });
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    setLastPoint(null);
    // Save current canvas state to history
    const canvas = canvasRef.current;
    const dataUrl = canvas.toDataURL();
    setHistory((prev) => [...prev, dataUrl]);
    setRedoStack([]);
  };

  // Toolbar and sidebar handlers
  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setHistory([]);
    setRedoStack([]);
  };

  const handleUndo = () => {
    if (history.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const newHistory = [...history];
    const last = newHistory.pop();
    setRedoStack((prev) => [...prev, last]);
    setHistory(newHistory);
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    if (newHistory.length > 0) {
      const img = new window.Image();
      img.src = newHistory[newHistory.length - 1];
      img.onload = () => ctx.drawImage(img, 0, 0);
    }
  };

  const handleRedo = () => {
    if (redoStack.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const newRedo = [...redoStack];
    const redoImg = newRedo.pop();
    setHistory((prev) => [...prev, redoImg]);
    setRedoStack(newRedo);
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const img = new window.Image();
    img.src = redoImg;
    img.onload = () => ctx.drawImage(img, 0, 0);
  };

  // --- Upload Image ---
  const handleUploadImage = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = function (event) {
      const img = new window.Image();
      img.onload = function () {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(img, 50, 50, 200, 200); // Draw at (50,50) with size 200x200
        // Save state after image upload
        const dataUrl = canvas.toDataURL();
        setHistory((prev) => [...prev, dataUrl]);
        setRedoStack([]);
      };
      img.src = event.target.result;
    };
    reader.readAsDataURL(file);
    // Reset input so same file can be uploaded again if needed
    e.target.value = '';
  };

  // --- Add Shape ---
  const handleAddShape = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = '#4f46e5'; // Indigo
    ctx.fillRect(300, 100, 120, 80); // x, y, width, height
    // Save state after adding shape
    const dataUrl = canvas.toDataURL();
    setHistory((prev) => [...prev, dataUrl]);
    setRedoStack([]);
  };

  // --- Add Text ---
  const handleAddText = () => {
    const text = prompt('Enter text to add:');
    if (!text) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.font = '24px Arial';
    ctx.fillStyle = '#111827';
    ctx.fillText(text, 100, 400);
    // Save state after adding text
    const dataUrl = canvas.toDataURL();
    setHistory((prev) => [...prev, dataUrl]);
    setRedoStack([]);
  };

  // --- Download ---
  const handleDownload = () => {
    const canvas = canvasRef.current;
    const link = document.createElement('a');
    link.download = 'canvas.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="flex flex-col h-screen">
      {/* Header Row */}
      <header className="flex justify-between items-center p-4 bg-gray-100 border-b">
        <div className="text-xl font-bold">MATTY</div> {/* LOGO */}
        <div className="flex gap-2">{/* Social media icons */}</div>
        <button className="px-4 py-2">About Us</button>
      </header>
      {/* Tools Bar */}
      <div className="flex gap-4 p-2 bg-gray-50 border-b">
        <button onClick={clearCanvas}>Clear All</button>
        <button onClick={handleUndo}>Undo</button>
        <button onClick={handleRedo}>Redo</button>
      </div>
      {/* Main Workspace */}
      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="flex flex-col gap-4 p-4 bg-gray-50 border-r">
          <button onClick={handleUploadImage}>Upload Image</button>
          <button onClick={handleAddShape}>Add Shape</button>
          <button onClick={handleAddText}>Add Text</button>
          <button onClick={handleDownload}>Download</button>
          {/* Hidden file input for image upload */}
          <input
            type="file"
            accept="image/*"
            ref={fileInputRef}
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
        </aside>
        {/* Canvas Area */}
        <main className="flex-1 flex items-center justify-center">
          <canvas
            ref={canvasRef}
            width={800}
            height={500}
            style={{ background: '#fff', border: '1px solid #eee' }}
            onMouseDown={startDrawing}
            onMouseMove={draw}
            onMouseUp={stopDrawing}
            onMouseLeave={stopDrawing}
          />
        </main>
      </div>
    </div>
  );
};

export default CanvasEditor;
