import React, { useRef, useEffect, useState } from 'react';

function drawPolygon(ctx, x, y, radius, sides, color) {
  ctx.beginPath();
  for (let i = 0; i < sides; i++) {
    const angle = (2 * Math.PI * i) / sides - Math.PI / 2;
    const dx = x + radius * Math.cos(angle);
    const dy = y + radius * Math.sin(angle);
    if (i === 0) ctx.moveTo(dx, dy);
    else ctx.lineTo(dx, dy);
  }
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
}

const initialState = {
  shapes: [], // {type, x, y, color, ...}
  texts: [],  // {text, x, y, color}
  lines: []   // [{points: [x1,y1,x2,y2,...], color}]
};

const CanvasEditor = () => {
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const [selectedShape, setSelectedShape] = useState('rectangle');
  const [shapeColor, setShapeColor] = useState('#4f46e5');
  const [drawMode, setDrawMode] = useState(false);
  const [textColor, setTextColor] = useState('#111827');
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [selectedTextIndex, setSelectedTextIndex] = useState(null);
  const [selectedShapeIndex, setSelectedShapeIndex] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentLine, setCurrentLine] = useState(null);
  const [history, setHistory] = useState([initialState]);
  const [historyIndex, setHistoryIndex] = useState(0);

  // Get current state from history
  const { shapes, texts, lines } = history[historyIndex];

  // Redraw canvas on every state change
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    // Draw shapes
    shapes.forEach((shape, idx) => {
      ctx.save();
      if (shape.type === 'image') {
        const img = new window.Image();
        img.src = shape.src;
        img.onload = () => {
          ctx.drawImage(img, shape.x, shape.y, shape.w, shape.h);
          if (idx === selectedShapeIndex) {
            ctx.strokeStyle = '#f59e42';
            ctx.lineWidth = 2;
            ctx.strokeRect(shape.x - 2, shape.y - 2, shape.w + 4, shape.h + 4);
          }
        };
        // If image is cached, draw immediately
        if (img.complete) {
          ctx.drawImage(img, shape.x, shape.y, shape.w, shape.h);
          if (idx === selectedShapeIndex) {
            ctx.strokeStyle = '#f59e42';
            ctx.lineWidth = 2;
            ctx.strokeRect(shape.x - 2, shape.y - 2, shape.w + 4, shape.h + 4);
          }
        }
      } else {
        ctx.fillStyle = shape.color;
        ctx.strokeStyle = shape.color;
        if (shape.type === 'rectangle') {
          ctx.fillRect(shape.x, shape.y, 120, 80);
        } else if (shape.type === 'square') {
          ctx.fillRect(shape.x, shape.y, 100, 100);
        } else if (shape.type === 'circle') {
          ctx.beginPath();
          ctx.arc(shape.x + 50, shape.y + 50, 50, 0, 2 * Math.PI);
          ctx.fill();
        } else if (shape.type === 'oval') {
          ctx.save();
          ctx.translate(shape.x + 60, shape.y + 40);
          ctx.scale(1.5, 1);
          ctx.beginPath();
          ctx.arc(0, 0, 40, 0, 2 * Math.PI);
          ctx.restore();
          ctx.fill();
        } else if (shape.type === 'triangle') {
          ctx.beginPath();
          ctx.moveTo(shape.x + 0, shape.y + 80);
          ctx.lineTo(shape.x + 120, shape.y + 80);
          ctx.lineTo(shape.x + 60, shape.y + 0);
          ctx.closePath();
          ctx.fill();
        } else if (shape.type === 'parallelogram') {
          ctx.beginPath();
          ctx.moveTo(shape.x + 20, shape.y + 80);
          ctx.lineTo(shape.x + 140, shape.y + 80);
          ctx.lineTo(shape.x + 120, shape.y + 0);
          ctx.lineTo(shape.x + 0, shape.y + 0);
          ctx.closePath();
          ctx.fill();
        } else if (shape.type === 'rhombus') {
          ctx.beginPath();
          ctx.moveTo(shape.x + 60, shape.y + 0);
          ctx.lineTo(shape.x + 120, shape.y + 40);
          ctx.lineTo(shape.x + 60, shape.y + 80);
          ctx.lineTo(shape.x + 0, shape.y + 40);
          ctx.closePath();
          ctx.fill();
        } else if (shape.type === 'trapezoid') {
          ctx.beginPath();
          ctx.moveTo(shape.x + 20, shape.y + 80);
          ctx.lineTo(shape.x + 100, shape.y + 80);
          ctx.lineTo(shape.x + 80, shape.y + 0);
          ctx.lineTo(shape.x + 40, shape.y + 0);
          ctx.closePath();
          ctx.fill();
        } else if (shape.type === 'kite') {
          ctx.beginPath();
          ctx.moveTo(shape.x + 60, shape.y + 0);
          ctx.lineTo(shape.x + 100, shape.y + 40);
          ctx.lineTo(shape.x + 60, shape.y + 80);
          ctx.lineTo(shape.x + 20, shape.y + 40);
          ctx.closePath();
          ctx.fill();
        } else if ([
          'pentagon', 'hexagon', 'heptagon', 'octagon', 'nonagon', 'decagon'
        ].includes(shape.type)) {
          const sides = {
            pentagon: 5, hexagon: 6, heptagon: 7, octagon: 8, nonagon: 9, decagon: 10
          }[shape.type];
          drawPolygon(ctx, shape.x + 60, shape.y + 60, 50, sides, shape.color);
        }
        if (idx === selectedShapeIndex) {
          ctx.strokeStyle = '#f59e42';
          ctx.lineWidth = 2;
          ctx.strokeRect(shape.x - 2, shape.y - 2, 124, 84);
        }
      }
      ctx.restore();
    });
    // Draw lines
    lines.forEach(line => {
      ctx.beginPath();
      ctx.strokeStyle = line.color || '#333';
      ctx.lineWidth = 2;
      line.points.forEach(([x, y], i) => {
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
    });
    // Draw texts
    texts.forEach(({ text, x, y, color }, idx) => {
      ctx.font = '24px Arial';
      ctx.fillStyle = color;
      ctx.fillText(text, x, y);
      if (idx === selectedTextIndex) {
        const width = ctx.measureText(text).width;
        ctx.strokeStyle = '#f59e42';
        ctx.strokeRect(x - 2, y - 24, width + 4, 28);
      }
    });
  }, [shapes, texts, lines, selectedTextIndex, selectedShapeIndex]);

  // --- Undo/Redo ---
  const saveState = (newShapes, newTexts, newLines) => {
    const newHistory = history.slice(0, historyIndex + 1);
    newHistory.push({
      shapes: JSON.parse(JSON.stringify(newShapes)),
      texts: JSON.parse(JSON.stringify(newTexts)),
      lines: JSON.parse(JSON.stringify(newLines))
    });
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
  };

  const handleUndo = () => {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      setSelectedTextIndex(null);
      setSelectedShapeIndex(null);
    }
  };
  const handleRedo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      setSelectedTextIndex(null);
      setSelectedShapeIndex(null);
    }
  };

  // --- Drawing (freehand) ---
  const handleCanvasMouseDown = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    // Check for text selection
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    for (let i = texts.length - 1; i >= 0; i--) {
      const { text, x, y } = texts[i];
      ctx.font = '24px Arial';
      const width = ctx.measureText(text).width;
      if (
        mouseX >= x - 2 &&
        mouseX <= x + width + 2 &&
        mouseY >= y - 24 &&
        mouseY <= y + 4
      ) {
        setSelectedTextIndex(i);
        setSelectedShapeIndex(null);
        setDragOffset({ x: mouseX - x, y: mouseY - y });
        setIsDragging(true);
        return;
      }
    }
    // Check for shape selection
    for (let i = shapes.length - 1; i >= 0; i--) {
      const shape = shapes[i];
      let w = 120, h = 80;
      if (shape.type === 'square') { w = 100; h = 100; }
      if (shape.type === 'circle') { w = 100; h = 100; }
      if (shape.type === 'oval') { w = 120; h = 80; }
      if (shape.type === 'image') { w = shape.w; h = shape.h; }
      if (
        mouseX >= shape.x &&
        mouseX <= shape.x + w &&
        mouseY >= shape.y &&
        mouseY <= shape.y + h
      ) {
        setSelectedShapeIndex(i);
        setSelectedTextIndex(null);
        setDragOffset({ x: mouseX - shape.x, y: mouseY - shape.y });
        setIsDragging(true);
        return;
      }
    }
    // If not on text or shape, start drawing if in draw mode
    if (drawMode) {
      setIsDrawing(true);
      setCurrentLine({ points: [[mouseX, mouseY]], color: '#333' });
    }
  };

  const handleCanvasMouseMove = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;
    if (isDrawing && drawMode) {
      setCurrentLine(line => ({ ...line, points: [...line.points, [mouseX, mouseY]] }));
      return;
    }
    if (isDragging && selectedTextIndex !== null) {
      const newTexts = texts.map((t, idx) =>
        idx === selectedTextIndex ? { ...t, x: mouseX - dragOffset.x, y: mouseY - dragOffset.y } : t
      );
      setHistory(h => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { shapes, texts: newTexts, lines };
        return newHistory;
      });
    }
    if (isDragging && selectedShapeIndex !== null) {
      const newShapes = shapes.map((s, idx) =>
        idx === selectedShapeIndex ? { ...s, x: mouseX - dragOffset.x, y: mouseY - dragOffset.y } : s
      );
      setHistory(h => {
        const newHistory = h.slice(0, historyIndex + 1);
        newHistory[historyIndex] = { shapes: newShapes, texts, lines };
        return newHistory;
      });
    }
  };

  const handleCanvasMouseUp = () => {
    if (isDrawing && drawMode && currentLine) {
      const newLines = [...lines, currentLine];
      saveState(shapes, texts, newLines);
      setIsDrawing(false);
      setCurrentLine(null);
    }
    if (isDragging && selectedTextIndex !== null) {
      saveState(shapes, texts, lines);
      setIsDragging(false);
    }
    if (isDragging && selectedShapeIndex !== null) {
      saveState(shapes, texts, lines);
      setIsDragging(false);
    }
    setSelectedTextIndex(null);
    setSelectedShapeIndex(null);
  };

  // --- Add Shape ---
  const handleAddShape = () => {
    let newShape = { type: selectedShape, x: 300, y: 100, color: shapeColor };
    if (selectedShape === 'image') return; // skip, handled by upload
    saveState([...shapes, newShape], texts, lines);
  };

  // --- Add Text ---
  const handleAddText = () => {
    const text = prompt('Enter text to add:');
    if (!text) return;
    saveState(shapes, [...texts, { text, x: 100, y: 400, color: textColor }], lines);
  };

  // --- Change color of selected text ---
  const handleTextColorChange = (e) => {
    setTextColor(e.target.value);
    if (selectedTextIndex !== null) {
      const newTexts = texts.map((t, idx) =>
        idx === selectedTextIndex ? { ...t, color: e.target.value } : t
      );
      saveState(shapes, newTexts, lines);
    }
  };

  // --- Change color of selected shape ---
  const handleShapeColorChange = (e) => {
    setShapeColor(e.target.value);
    if (selectedShapeIndex !== null) {
      const newShapes = shapes.map((s, idx) =>
        idx === selectedShapeIndex ? { ...s, color: e.target.value } : s
      );
      saveState(newShapes, texts, lines);
    }
  };

  // --- Upload Image ---
  const handleUploadImage = () => {
    fileInputRef.current.click();
  };
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = function (event) {
      // Add image as a shape to history
      const newShape = {
        type: 'image',
        x: 50,
        y: 50,
        w: 200,
        h: 200,
        src: event.target.result
      };
      saveState([...shapes, newShape], texts, lines);
    };
    reader.readAsDataURL(file);
    e.target.value = '';
  };

  // --- Download ---
  const handleDownload = () => {
    const canvas = canvasRef.current;
    const link = document.createElement('a');
    link.download = 'canvas.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  // --- Clear All ---
  const clearCanvas = () => {
    saveState([], [], []);
    setSelectedTextIndex(null);
    setSelectedShapeIndex(null);
  };

  return (
    <div className="flex flex-col h-screen">
      {/* Header Row */}
      <header className="flex justify-between items-center p-4 bg-gray-100 border-b">
        <div className="text-xl font-bold">MATTY</div>
        <div className="flex gap-2">{/* Social media icons */}</div>
        <button className="px-4 py-2">About Us</button>
      </header>
      {/* Tools Bar */}
      <div className="flex gap-4 p-2 bg-gray-50 border-b">
        <button onClick={clearCanvas}>Clear All</button>
        <button onClick={handleUndo} disabled={historyIndex === 0}>Undo</button>
        <button onClick={handleRedo} disabled={historyIndex === history.length - 1}>Redo</button>
      </div>
      {/* Main Workspace */}
      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="flex flex-col gap-4 p-4 bg-gray-50 border-r">
          <button
            className={drawMode ? 'font-bold underline' : ''}
            onClick={() => setDrawMode(true)}
          >
            Pencil/Draw
          </button>
          <label>
            <span className="block mb-1">Shape</span>
            <select
              value={selectedShape}
              onChange={e => {
                setSelectedShape(e.target.value);
                setDrawMode(false);
              }}
              className="mb-2 p-1 border rounded"
            >
              <option value="rectangle">Rectangle</option>
              <option value="square">Square</option>
              <option value="circle">Circle</option>
              <option value="oval">Oval</option>
              <option value="triangle">Triangle</option>
              <option value="parallelogram">Parallelogram</option>
              <option value="rhombus">Rhombus</option>
              <option value="trapezoid">Trapezoid</option>
              <option value="kite">Kite</option>
              <option value="pentagon">Pentagon</option>
              <option value="hexagon">Hexagon</option>
              <option value="heptagon">Heptagon</option>
              <option value="octagon">Octagon</option>
              <option value="nonagon">Nonagon</option>
              <option value="decagon">Decagon</option>
            </select>
          </label>
          <label>
            <span className="block mb-1">Shape Color</span>
            <input
              type="color"
              value={shapeColor}
              onChange={handleShapeColorChange}
              className="w-8 h-8 p-0 border-0"
            />
          </label>
          <label>
            <span className="block mb-1">Text Color</span>
            <input
              type="color"
              value={textColor}
              onChange={handleTextColorChange}
              className="w-8 h-8 p-0 border-0"
            />
          </label>
          <button onClick={handleUploadImage}>Upload Image</button>
          <button onClick={handleAddShape}>Add Shape</button>
          <button onClick={handleAddText}>Add Text</button>
          <button onClick={handleDownload}>Download</button>
          <input
            type="file"
            accept="image/*"
            ref={fileInputRef}
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
        </aside>
        <main className="flex-1 flex items-center justify-center">
          <canvas
            ref={canvasRef}
            width={800}
            height={500}
            style={{ background: '#fff', border: '1px solid #eee' }}
            onMouseDown={handleCanvasMouseDown}
            onMouseMove={handleCanvasMouseMove}
            onMouseUp={handleCanvasMouseUp}
            onMouseLeave={handleCanvasMouseUp}
          />
        </main>
      </div>
    </div>
  );
};

export default CanvasEditor;
